import React, { useState, useEffect } from 'react';

// Professional SVG Icons
const Icons = {
  Target: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="12" cy="12" r="10"/>
      <circle cx="12" cy="12" r="6"/>
      <circle cx="12" cy="12" r="2"/>
    </svg>
  ),
  Zap: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polygon points="13,2 3,14 12,14 11,22 21,10 12,10"/>
    </svg>
  ),
  Star: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polygon points="12,2 15.09,8.26 22,9 17,14.74 18.18,21.02 12,17.77 5.82,21.02 7,14.74 2,9 8.91,8.26"/>
    </svg>
  ),
  BarChart: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <line x1="12" y1="20" x2="12" y2="10"/>
      <line x1="18" y1="20" x2="18" y2="4"/>
      <line x1="6" y1="20" x2="6" y2="16"/>
    </svg>
  ),
  Settings: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="12" cy="12" r="3"/>
      <path d="M12 1v6m0 6v6m11-7h-6m-6 0H1m17-4a4 4 0 01-8 0m8 0a4 4 0 00-8 0m-4 8a4 4 0 108 0m-8 0a4 4 0 008 0"/>
    </svg>
  ),
  Search: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="11" cy="11" r="8"/>
      <path d="m21 21-4.35-4.35"/>
    </svg>
  ),
  X: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <line x1="18" y1="6" x2="6" y2="18"/>
      <line x1="6" y1="6" x2="18" y2="18"/>
    </svg>
  ),
  ChevronRight: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polyline points="9,18 15,12 9,6"/>
    </svg>
  ),
  User: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
      <circle cx="12" cy="7" r="4"/>
    </svg>
  ),
  TrendingUp: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polyline points="23,6 13.5,15.5 8.5,10.5 1,18"/>
      <polyline points="17,6 23,6 23,12"/>
    </svg>
  )
};

// Enhanced animated background component
const CalculatorBackground = () => (
  <div className="fixed inset-0 pointer-events-none overflow-hidden">
    {/* Floating particles */}
    {[...Array(15)].map((_, i) => (
      <div
        key={i}
        className="absolute w-1 h-1 bg-anime-primary/30 rounded-full"
        style={{
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          animationDelay: `${Math.random() * 5}s`,
          animation: `float ${Math.random() * 3 + 4}s ease-in-out infinite`
        }}
      />
    ))}

    {/* Subtle gradient overlay */}
    <div className="absolute inset-0 bg-gradient-to-br from-anime-primary/3 via-transparent to-anime-secondary/3 opacity-50" />

    {/* Minimal grid pattern */}
    <div className="absolute inset-0 opacity-[0.015]"
         style={{
           backgroundImage: `
             linear-gradient(rgba(0, 217, 255, 0.3) 1px, transparent 1px),
             linear-gradient(90deg, rgba(0, 217, 255, 0.3) 1px, transparent 1px)
           `,
           backgroundSize: '60px 60px'
         }} />
  </div>
);

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythical',
      element: 'Holy',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythical',
      element: 'Fire',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Spark',
      tier: 'SemiMeta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1100,
        range: 8.0,
        sps: 1.3,
        placement: 30000
      }
    },
    {
      id: 5,
      name: 'Naruto Six Paths',
      rarity: 'Mythical',
      element: 'Nature',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1220,
        range: 8.8,
        sps: 1.25,
        placement: 33000
      }
    },
    {
      id: 6,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Unbound',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1500,
        range: 9.5,
        sps: 1.0,
        placement: 40000
      }
    },
    {
      id: 7,
      name: 'Sukuna',
      rarity: 'Mythical',
      element: 'Curse',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1280,
        range: 8.3,
        sps: 1.15,
        placement: 34000
      }
    },
    {
      id: 8,
      name: 'Aizen Sosuke',
      rarity: 'Legendary',
      element: 'Passion',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1050,
        range: 9.0,
        sps: 1.4,
        placement: 29000
      }
    },
    {
      id: 9,
      name: 'Vegeta Blue',
      rarity: 'Epic',
      element: 'Blast',
      tier: 'Support',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 950,
        range: 7.5,
        sps: 1.6,
        placement: 25000
      }
    },
    {
      id: 10,
      name: 'Luffy Gear 5',
      rarity: 'Legendary',
      element: 'Cosmic',
      tier: 'SemiMeta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1150,
        range: 8.2,
        sps: 1.35,
        placement: 31000
      }
    }
  ];

  // Available traits with their effects
  const availableTraits = [
    {
      id: 'blitz',
      name: 'Blitz',
      description: '+15% SPS, -10% Range',
      icon: '/images/Traits/Blitz.webp',
      effects: {
        sps: 1.15,
        range: 0.9
      }
    },
    {
      id: 'deadeye', 
      name: 'Deadeye',
      description: '+20% Range, -5% SPS',
      icon: '/images/Traits/Deadeye.webp',
      effects: {
        range: 1.2,
        sps: 0.95
      }
    },
    {
      id: 'ethereal',
      name: 'Ethereal',
      description: '+10% Damage, +5% SPS',
      icon: '/images/Traits/Ethereal.webp',
      effects: {
        damage: 1.1,
        sps: 1.05
      }
    },
    {
      id: 'fortune',
      name: 'Fortune',
      description: '+25% Damage vs Bosses',
      icon: '/images/Traits/Fortune.webp',
      effects: {
        damage: 1.25
      }
    },
    {
      id: 'marksman',
      name: 'Marksman',
      description: '+15% Range, +10% Damage',
      icon: '/images/Traits/Marksman.webp',
      effects: {
        range: 1.15,
        damage: 1.1
      }
    },
    {
      id: 'monarch',
      name: 'Monarch',
      description: '+20% All Stats',
      icon: '/images/Traits/Monarch.webp',
      effects: {
        damage: 1.2,
        range: 1.2,
        sps: 1.2
      }
    },
    {
      id: 'range',
      name: 'Range',
      description: '+25% Range',
      icon: '/images/Traits/Range.webp',
      effects: {
        range: 1.25
      }
    },
    {
      id: 'scholar',
      name: 'Scholar',
      description: '+10% SPS, +5% Range',
      icon: '/images/Traits/Scholar.webp',
      effects: {
        sps: 1.1,
        range: 1.05
      }
    },
    {
      id: 'solar',
      name: 'Solar',
      description: '+15% Damage',
      icon: '/images/Traits/Solar.webp',
      effects: {
        damage: 1.15
      }
    },
    {
      id: 'swift',
      name: 'Swift',
      description: '+20% SPS',
      icon: '/images/Traits/Swift.webp',
      effects: {
        sps: 1.2
      }
    },
    {
      id: 'vigor',
      name: 'Vigor',
      description: '+12% Damage, +8% SPS',
      icon: '/images/Traits/Vigor.webp',
      effects: {
        damage: 1.12,
        sps: 1.08
      }
    }
  ];

  // Stat grades configuration
  const statGrades = {
    'GODLY': {
      damageRange: [25, 30],
      rangeRange: [12.5, 15],
      spaRange: [12.5, 15],
      color: 'from-yellow-400 to-yellow-600',
      textColor: 'text-yellow-400'
    },
    'Z+': {
      damageRange: [20, 24.99],
      rangeRange: [10, 12.49],
      spaRange: [10, 12.49],
      color: 'from-red-400 to-red-600',
      textColor: 'text-red-400'
    },
    'Z': {
      damageRange: [15, 19.99],
      rangeRange: [7.5, 9.99],
      spaRange: [7.5, 9.99],
      color: 'from-purple-400 to-purple-600',
      textColor: 'text-purple-400'
    },
    'S': {
      damageRange: [12, 14.99],
      rangeRange: [6, 7.49],
      spaRange: [6, 7.49],
      color: 'from-blue-400 to-blue-600',
      textColor: 'text-blue-400'
    },
    'A': {
      damageRange: [9, 11.99],
      rangeRange: [4.5, 5.99],
      spaRange: [4.5, 5.99],
      color: 'from-green-400 to-green-600',
      textColor: 'text-green-400'
    },
    'B': {
      damageRange: [6, 8.99],
      rangeRange: [3, 4.49],
      spaRange: [3, 4.49],
      color: 'from-yellow-400 to-yellow-600',
      textColor: 'text-yellow-400'
    },
    'C': {
      damageRange: [3, 5.99],
      rangeRange: [1.5, 2.99],
      spaRange: [1.5, 2.99],
      color: 'from-orange-400 to-orange-600',
      textColor: 'text-orange-400'
    },
    'D': {
      damageRange: [0, 2.99],
      rangeRange: [0, 1.49],
      spaRange: [0, 1.49],
      color: 'from-red-400 to-red-600',
      textColor: 'text-red-400'
    }
  };

  // State management
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [unitSearch, setUnitSearch] = useState('');
  const [level, setLevel] = useState(1);
  const [upgrade, setUpgrade] = useState(1);
  const [statGradeSelection, setStatGradeSelection] = useState({
    damage: 'B',
    range: 'B',
    sps: 'B'
  });
  const [statPercentages, setStatPercentages] = useState({
    damage: 9,
    range: 4.75,
    sps: 4.75
  });
  const [selectedTrait, setSelectedTrait] = useState(null);
  const [calculatedStats, setCalculatedStats] = useState(null);
  const [activeTab, setActiveTab] = useState('unit');

  // Filter units based on search
  const filteredUnits = unitsDatabase.filter(unit =>
    unit.name.toLowerCase().includes(unitSearch.toLowerCase())
  );

  // Helper function to get stat range key
  const getStatRangeKey = (stat) => {
    if (stat === 'damage') return 'damageRange';
    if (stat === 'sps') return 'spaRange';
    if (stat === 'range') return 'rangeRange';
    if (stat === 'placement') return 'damageRange';
    return 'damageRange';
  };

  // Calculate final stats
  const calculateStats = () => {
    if (!selectedUnit) return null;

    const baseStats = selectedUnit.baseStats;
    const levelMultiplier = 1 + (level - 1) * 0.1;
    const upgradeMultiplier = 1 + (upgrade - 1) * 0.05;

    let finalStats = {};

    Object.keys(baseStats).forEach(stat => {
      if (stat === 'placement') {
        finalStats[stat] = baseStats[stat];
        return;
      }

      const percentageBonus = statPercentages[stat] / 100;
      const statMultiplier = 1 + percentageBonus;
      let statValue = baseStats[stat] * levelMultiplier * upgradeMultiplier * statMultiplier;

      if (selectedTrait) {
        const trait = availableTraits.find(t => t.id === selectedTrait);
        if (trait && trait.effects[stat]) {
          statValue *= trait.effects[stat];
        }
      }

      finalStats[stat] = Math.round(statValue * 100) / 100;
    });

    return finalStats;
  };

  // Update calculated stats when dependencies change
  useEffect(() => {
    setCalculatedStats(calculateStats());
  }, [selectedUnit, level, upgrade, statPercentages, selectedTrait]);

  // Handle stat percentage changes
  const handleStatPercentageChange = (statType, value) => {
    const percentage = parseFloat(value);
    setStatPercentages(prev => ({
      ...prev,
      [statType]: percentage
    }));

    const rangeKey = getStatRangeKey(statType);
    let newGrade = 'D';

    for (const [grade, data] of Object.entries(statGrades)) {
      const [min, max] = data[rangeKey];
      if (percentage >= min && percentage <= max) {
        newGrade = grade;
        break;
      }
    }

    setStatGradeSelection(prev => ({
      ...prev,
      [statType]: newGrade
    }));
  };

  // Handle trait selection
  const handleTraitSelect = (traitId) => {
    setSelectedTrait(prev => prev === traitId ? null : traitId);
  };

  // Handle unit selection
  const handleUnitSelect = (unit) => {
    setSelectedUnit(unit);
    setActiveTab('stats');
  };

  // Clear selection
  const clearSelection = () => {
    setSelectedUnit(null);
    setSelectedTrait(null);
    setLevel(1);
    setUpgrade(1);
    setStatPercentages({
      damage: 9,
      range: 4.75,
      sps: 4.75
    });
    setStatGradeSelection({
      damage: 'B',
      range: 'B',
      sps: 'B'
    });
    setActiveTab('unit');
  };

  // Get element image
  const getElementImage = (element) => {
    const elementMap = {
      'Holy': '/images/Elements/Holy (1).webp',
      'Water': '/images/Elements/Water.webp',
      'Fire': '/images/Elements/Fire.webp',
      'Spark': '/images/Elements/Spark.webp',
      'Nature': '/images/Elements/Nature.webp',
      'Unbound': '/images/Elements/Unbound.webp',
      'Curse': '/images/Elements/Curse.webp',
      'Passion': '/images/Elements/Passion.webp',
      'Blast': '/images/Elements/Blast.webp',
      'Cosmic': '/images/Elements/Cosmic.webp'
    };
    return elementMap[element] || '/images/Elements/UnknownElement.webp';
  };

  // Get tier image
  const getTierImage = (tier) => {
    const tierMap = {
      'Meta': '/images/UnitTiers/Meta.webp',
      'SemiMeta': '/images/UnitTiers/SemiMeta.webp',
      'Good': '/images/UnitTiers/Good.webp',
      'Support': '/images/UnitTiers/Support.webp',
      'MetaSupport': '/images/UnitTiers/MetaSupport.webp',
      'Bad': '/images/UnitTiers/Bad.webp',
      'Broken': '/images/UnitTiers/Broken.webp'
    };
    return tierMap[tier] || '/images/UnitTiers/Support.webp';
  };

  // Get rarity styling
  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Secret': return 'border-red-400/40 bg-red-500/5';
      case 'Mythical': return 'border-purple-400/40 bg-purple-500/5';
      case 'Legendary': return 'border-yellow-400/40 bg-yellow-500/5';
      case 'Epic': return 'border-blue-400/40 bg-blue-500/5';
      case 'Rare': return 'border-green-400/40 bg-green-500/5';
      default: return 'border-slate-400/30 bg-slate-500/5';
    }
  };

  return (
    <div className="min-h-screen relative bg-slate-950">
      <CalculatorBackground />

      {/* Professional Header */}
      <div className="relative z-10 border-b border-white/5 bg-slate-900/80 backdrop-blur-xl">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-semibold text-white tracking-tight">
                Unit Statistics Calculator
              </h1>
              <p className="text-slate-400 mt-1 text-sm">
                Optimize unit builds with precise calculations and analysis
              </p>
            </div>
            {selectedUnit && (
              <button
                onClick={clearSelection}
                className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-slate-300 hover:text-white bg-slate-800 hover:bg-slate-700 border border-slate-700 rounded-lg transition-colors"
              >
                <Icons.X className="w-4 h-4" />
                Clear Selection
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="relative z-10 max-w-7xl mx-auto px-6 py-8">
        {/* Professional Navigation */}
        <div className="mb-8">
          <nav className="flex space-x-1 bg-slate-900/50 backdrop-blur-sm p-1 rounded-xl border border-white/5">
            {[
              { id: 'unit', label: 'Unit Selection', icon: Icons.Target },
              { id: 'stats', label: 'Configuration', icon: Icons.Settings, disabled: !selectedUnit },
              { id: 'traits', label: 'Traits', icon: Icons.Star, disabled: !selectedUnit },
              { id: 'results', label: 'Results', icon: Icons.BarChart, disabled: !selectedUnit }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => !tab.disabled && setActiveTab(tab.id)}
                disabled={tab.disabled}
                className={`flex items-center gap-2 px-4 py-2.5 text-sm font-medium rounded-lg transition-all ${
                  activeTab === tab.id 
                    ? 'bg-anime-primary text-slate-900 shadow-lg' 
                    : tab.disabled 
                      ? 'text-slate-500 cursor-not-allowed' 
                      : 'text-slate-300 hover:text-white hover:bg-slate-800/50'
                }`}
              >
                <tab.icon className="w-4 h-4" />
                {tab.label}
              </button>
            ))}
          </nav>
        </div>

        {/* Tab Content */}
        <div className="space-y-8">
          {/* Unit Selection Tab */}
          {activeTab === 'unit' && (
            <div className="bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-8">
              <div className="flex items-center gap-3 mb-8">
                <Icons.Target className="w-6 h-6 text-anime-primary" />
                <h2 className="text-xl font-semibold text-white">Select Unit</h2>
              </div>

              {/* Professional Search */}
              <div className="relative mb-8">
                <Icons.Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-slate-400" />
                <input
                  type="text"
                  placeholder="Search units by name..."
                  value={unitSearch}
                  onChange={(e) => setUnitSearch(e.target.value)}
                  className="w-full pl-10 pr-4 py-3 bg-slate-800/50 border border-slate-700 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-anime-primary/50 focus:border-anime-primary transition-colors"
                />
              </div>

              {/* Clean Units Grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                {filteredUnits.map((unit) => (
                  <button
                    key={unit.id}
                    onClick={() => handleUnitSelect(unit)}
                    className={`group p-4 rounded-xl border transition-all hover:scale-[1.02] ${
                      selectedUnit?.id === unit.id 
                        ? 'border-anime-primary bg-anime-primary/10 shadow-lg shadow-anime-primary/20' 
                        : `${getRarityColor(unit.rarity)} hover:border-white/20`
                    }`}
                  >
                    <div className="flex flex-col items-center text-center">
                      <div className="relative mb-3">
                        <img
                          src={unit.image}
                          alt={unit.name}
                          className="w-16 h-16 rounded-xl object-cover border border-white/10"
                        />
                        <div className="absolute -top-1 -right-1 flex gap-1">
                          <img src={getElementImage(unit.element)} alt={unit.element} className="w-5 h-5 rounded" />
                          <img src={getTierImage(unit.tier)} alt={unit.tier} className="w-5 h-5 rounded" />
                        </div>
                      </div>
                      <h3 className="font-medium text-white text-sm mb-1 group-hover:text-anime-primary transition-colors">
                        {unit.name}
                      </h3>
                      <div className="flex items-center gap-2 text-xs text-slate-400">
                        <span>{unit.rarity}</span>
                        <span></span>
                        <span>{unit.tier}</span>
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Configuration Tab */}
          {activeTab === 'stats' && selectedUnit && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Level & Upgrade Configuration */}
              <div className="bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-6">
                <div className="flex items-center gap-3 mb-6">
                  <Icons.Zap className="w-5 h-5 text-anime-primary" />
                  <h3 className="text-lg font-semibold text-white">Level & Upgrade</h3>
                </div>

                <div className="space-y-8">
                  {/* Level Control */}
                  <div>
                    <div className="flex items-center justify-between mb-4">
                      <label className="text-sm font-medium text-slate-300">Level</label>
                      <div className="flex items-center gap-2">
                        <span className="text-2xl font-bold text-anime-primary">{level}</span>
                        <span className="text-xs text-slate-500">/ 60</span>
                      </div>
                    </div>
                    <input
                      type="range"
                      min="1"
                      max="60"
                      value={level}
                      onChange={(e) => setLevel(parseInt(e.target.value))}
                      className="w-full h-2 bg-slate-800 rounded-lg appearance-none cursor-pointer slider"
                    />
                    <div className="flex justify-between text-xs text-slate-500 mt-2">
                      <span>1</span>
                      <span>60</span>
                    </div>
                  </div>

                  {/* Upgrade Control */}
                  <div>
                    <div className="flex items-center justify-between mb-4">
                      <label className="text-sm font-medium text-slate-300">Upgrade</label>
                      <div className="flex items-center gap-2">
                        <span className="text-2xl font-bold text-orange-400">{upgrade}</span>
                        <span className="text-xs text-slate-500">/ 10</span>
                      </div>
                    </div>
                    <input
                      type="range"
                      min="1"
                      max="10"
                      value={upgrade}
                      onChange={(e) => setUpgrade(parseInt(e.target.value))}
                      className="w-full h-2 bg-slate-800 rounded-lg appearance-none cursor-pointer slider"
                    />
                    <div className="flex justify-between text-xs text-slate-500 mt-2">
                      <span>1</span>
                      <span>10</span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Stat Configuration */}
              <div className="bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-6">
                <div className="flex items-center gap-3 mb-6">
                  <Icons.TrendingUp className="w-5 h-5 text-anime-primary" />
                  <h3 className="text-lg font-semibold text-white">Stat Grading</h3>
                </div>

                <div className="space-y-6">
                  {['damage', 'range', 'sps'].map((statType) => {
                    const rangeKey = getStatRangeKey(statType);
                    const currentGrade = statGradeSelection[statType];
                    const gradeData = statGrades[currentGrade];
                    const maxRange = gradeData[rangeKey][1];

                    return (
                      <div key={statType} className="space-y-3">
                        <div className="flex items-center justify-between">
                          <label className="text-sm font-medium text-slate-300 capitalize">
                            {statType === 'sps' ? 'SPS' : statType}
                          </label>
                          <div className="flex items-center gap-3">
                            <span className={`text-sm font-bold px-2 py-1 rounded ${gradeData.textColor} bg-current/10`}>
                              {currentGrade}
                            </span>
                            <span className="text-sm font-medium text-white">
                              {statPercentages[statType]}%
                            </span>
                          </div>
                        </div>
                        <input
                          type="range"
                          min="0"
                          max={maxRange}
                          step="0.01"
                          value={statPercentages[statType]}
                          onChange={(e) => handleStatPercentageChange(statType, e.target.value)}
                          className="w-full h-2 bg-slate-800 rounded-lg appearance-none cursor-pointer slider"
                        />
                        <div className="flex justify-between text-xs text-slate-500">
                          <span>0%</span>
                          <span>{maxRange}%</span>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          )}

          {/* Traits Tab */}
          {activeTab === 'traits' && selectedUnit && (
            <div className="bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-8">
              <div className="flex items-center gap-3 mb-8">
                <Icons.Star className="w-6 h-6 text-anime-primary" />
                <h3 className="text-xl font-semibold text-white">Select Trait</h3>
                <span className="text-sm text-slate-400">Optional enhancement</span>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {availableTraits.map((trait) => (
                  <button
                    key={trait.id}
                    onClick={() => handleTraitSelect(trait.id)}
                    className={`group p-4 rounded-xl border transition-all hover:scale-[1.02] ${
                      selectedTrait === trait.id 
                        ? 'border-anime-primary bg-anime-primary/10 shadow-lg shadow-anime-primary/20' 
                        : 'border-white/5 bg-slate-800/30 hover:border-white/10 hover:bg-slate-800/50'
                    }`}
                  >
                    <div className="flex flex-col items-center text-center">
                      <img
                        src={trait.icon}
                        alt={trait.name}
                        className="w-12 h-12 rounded-lg mb-3"
                      />
                      <h4 className="font-medium text-white mb-2 group-hover:text-anime-primary transition-colors">
                        {trait.name}
                      </h4>
                      <p className="text-xs text-slate-400 leading-relaxed">
                        {trait.description}
                      </p>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Results Tab */}
          {activeTab === 'results' && selectedUnit && calculatedStats && (
            <div className="grid grid-cols-1 lg:grid-cols-5 gap-8">
              {/* Configuration Summary */}
              <div className="lg:col-span-2 bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-6">
                <div className="flex items-center gap-3 mb-6">
                  <Icons.User className="w-5 h-5 text-anime-primary" />
                  <h3 className="text-lg font-semibold text-white">Configuration</h3>
                </div>

                <div className="space-y-4">
                  <div className="flex items-center gap-4 p-4 bg-slate-800/30 rounded-xl">
                    <img src={selectedUnit.image} alt={selectedUnit.name} className="w-12 h-12 rounded-lg" />
                    <div>
                      <h4 className="font-medium text-white">{selectedUnit.name}</h4>
                      <p className="text-sm text-slate-400">Level {level}  Upgrade {upgrade}</p>
                    </div>
                  </div>

                  {selectedTrait && (
                    <div className="flex items-center gap-4 p-4 bg-anime-primary/5 border border-anime-primary/20 rounded-xl">
                      <img src={availableTraits.find(t => t.id === selectedTrait).icon} alt="" className="w-10 h-10 rounded-lg" />
                      <div>
                        <h5 className="font-medium text-anime-primary">
                          {availableTraits.find(t => t.id === selectedTrait).name}
                        </h5>
                        <p className="text-xs text-slate-400">
                          {availableTraits.find(t => t.id === selectedTrait).description}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Final Statistics */}
              <div className="lg:col-span-3 bg-slate-900/50 backdrop-blur-sm border border-white/5 rounded-2xl p-6">
                <div className="flex items-center gap-3 mb-6">
                  <Icons.BarChart className="w-5 h-5 text-anime-primary" />
                  <h3 className="text-lg font-semibold text-white">Final Statistics</h3>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  {Object.entries(calculatedStats).map(([stat, value]) => (
                    <div key={stat} className="p-4 bg-slate-800/30 rounded-xl">
                      <div className="text-sm font-medium text-slate-400 capitalize mb-1">
                        {stat === 'sps' ? 'SPS' : stat}
                      </div>
                      <div className="text-2xl font-bold text-white">
                        {stat === 'placement' ? value.toLocaleString() : value}
                        <span className="text-sm font-normal text-slate-400 ml-1">
                          {stat === 'range' ? 'm' : stat === 'sps' ? '/s' : ''}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Calculator;
