import React, { useState, useEffect } from 'react';

// Professional SVG Icons
const Icons = {
  Target: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="12" cy="12" r="10"/>
      <circle cx="12" cy="12" r="6"/>
      <circle cx="12" cy="12" r="2"/>
    </svg>
  ),
  Zap: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polygon points="13,2 3,14 12,14 11,22 21,10 12,10"/>
    </svg>
  ),
  Star: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polygon points="12,2 15.09,8.26 22,9 17,14.74 18.18,21.02 12,17.77 5.82,21.02 7,14.74 2,9 8.91,8.26"/>
    </svg>
  ),
  BarChart: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <line x1="12" y1="20" x2="12" y2="10"/>
      <line x1="18" y1="20" x2="18" y2="4"/>
      <line x1="6" y1="20" x2="6" y2="16"/>
    </svg>
  ),
  Settings: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="12" cy="12" r="3"/>
      <path d="M12 1v6m0 6v6m11-7h-6m-6 0H1m17-4a4 4 0 01-8 0m8 0a4 4 0 00-8 0m-4 8a4 4 0 108 0m-8 0a4 4 0 008 0"/>
    </svg>
  ),
  Search: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <circle cx="11" cy="11" r="8"/>
      <path d="m21 21-4.35-4.35"/>
    </svg>
  ),
  X: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <line x1="18" y1="6" x2="6" y2="18"/>
      <line x1="6" y1="6" x2="18" y2="18"/>
    </svg>
  ),
  ChevronRight: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polyline points="9,18 15,12 9,6"/>
    </svg>
  ),
  User: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
      <circle cx="12" cy="7" r="4"/>
    </svg>
  ),
  TrendingUp: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <polyline points="23,6 13.5,15.5 8.5,10.5 1,18"/>
      <polyline points="17,6 23,6 23,12"/>
    </svg>
  ),
  Calculator: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <rect x="4" y="2" width="16" height="20" rx="2"/>
      <line x1="8" y1="6" x2="16" y2="6"/>
      <line x1="8" y1="10" x2="16" y2="10"/>
      <line x1="8" y1="14" x2="16" y2="14"/>
      <line x1="8" y1="18" x2="12" y2="18"/>
    </svg>
  ),
  Menu: ({ className = "w-5 h-5" }) => (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <line x1="3" y1="6" x2="21" y2="6"/>
      <line x1="3" y1="12" x2="21" y2="12"/>
      <line x1="3" y1="18" x2="21" y2="18"/>
    </svg>
  )
};

// Enhanced animated background component
const CalculatorBackground = () => (
  <div className="fixed inset-0 pointer-events-none overflow-hidden">
    {[...Array(8)].map((_, i) => (
      <div
        key={i}
        className="absolute w-0.5 h-0.5 bg-anime-primary/15 rounded-full"
        style={{
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          animationDelay: `${Math.random() * 5}s`,
          animation: `float ${Math.random() * 4 + 8}s ease-in-out infinite`
        }}
      />
    ))}
    <div className="absolute inset-0 bg-gradient-to-br from-anime-primary/1 via-transparent to-anime-secondary/1 opacity-30" />
  </div>
);

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythical',
      element: 'Holy',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythical',
      element: 'Fire',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Spark',
      tier: 'SemiMeta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1100,
        range: 8.0,
        sps: 1.3,
        placement: 30000
      }
    },
    {
      id: 5,
      name: 'Naruto Six Paths',
      rarity: 'Mythical',
      element: 'Nature',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1220,
        range: 8.8,
        sps: 1.25,
        placement: 33000
      }
    },
    {
      id: 6,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Unbound',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1500,
        range: 9.5,
        sps: 1.0,
        placement: 40000
      }
    },
    {
      id: 7,
      name: 'Sukuna',
      rarity: 'Mythical',
      element: 'Curse',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1280,
        range: 8.3,
        sps: 1.15,
        placement: 34000
      }
    },
    {
      id: 8,
