import React, { useState, useEffect } from 'react';

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythic',
      element: 'Light',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythic',
      element: 'Fire',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Dark',
      baseStats: {
        damage: 1050,
        range: 8.0,
        sps: 1.3,
        placement: 29500
      }
    },
    {
      id: 5,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Physical',
      baseStats: {
        damage: 1650,
        range: 6.5,
        sps: 0.8,
        placement: 45000
      }
    },
    {
      id: 6,
      name: 'Gojo Satoru',
      rarity: 'Mythic',
      element: 'Light',
      baseStats: {
        damage: 1320,
        range: 10.5,
        sps: 1.0,
        placement: 38000
      }
    }
  ];

  // Stat grades and their multipliers
  const statGrades = {
    'GODLY': { multiplier: 2.0, color: 'text-red-400', bgColor: 'bg-red-500/20' },
    'Z+': { multiplier: 1.8, color: 'text-orange-400', bgColor: 'bg-orange-500/20' },
    'Z': { multiplier: 1.6, color: 'text-yellow-400', bgColor: 'bg-yellow-500/20' },
    'S': { multiplier: 1.4, color: 'text-green-400', bgColor: 'bg-green-500/20' },
    'A': { multiplier: 1.2, color: 'text-blue-400', bgColor: 'bg-blue-500/20' },
    'B': { multiplier: 1.0, color: 'text-purple-400', bgColor: 'bg-purple-500/20' },
    'C': { multiplier: 0.8, color: 'text-gray-400', bgColor: 'bg-gray-500/20' },
    'D': { multiplier: 0.6, color: 'text-red-600', bgColor: 'bg-red-600/20' }
  };

  // Available traits and their effects
  const availableTraits = [
    { 
      id: 'monarch', 
      name: 'Monarch', 
      rarity: '0.1%',
      effects: { damage: 4.0, sps: 0.9, range: 1.05 },
      description: '+300% Damage, -10% SPA, +5% Range, ONE PLACEMENT only',
      color: 'text-red-500',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-500'
    },
    { 
      id: 'ethereal', 
      name: 'Ethereal', 
      rarity: '0.175%',
      effects: { damage: 1.2, sps: 0.8, range: 1.05 },
      description: '+20% Damage, -20% SPA, +5% Range',
      color: 'text-purple-400',
      bgColor: 'bg-purple-500/20',
      borderColor: 'border-purple-400'
    },
    { 
      id: 'deadeye', 
      name: 'Deadeye', 
      rarity: '0.375%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-orange-400',
      bgColor: 'bg-orange-500/20',
      borderColor: 'border-orange-400'
    },
    { 
      id: 'solar', 
      name: 'Solar', 
      rarity: '0.5%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-yellow-400',
      bgColor: 'bg-yellow-500/20',
      borderColor: 'border-yellow-400'
    },
    { 
      id: 'blitz', 
      name: 'Blitz', 
      rarity: '1.85%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction',
      color: 'text-cyan-400',
      bgColor: 'bg-cyan-500/20',
      borderColor: 'border-cyan-400'
    },
    { 
      id: 'fortune', 
      name: 'Fortune', 
      rarity: '2.5%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction, +20% Cash (Farm), -10% Upgrade cost (Non-farm)',
      color: 'text-green-400',
      bgColor: 'bg-green-500/20',
      borderColor: 'border-green-400'
    },
    { 
      id: 'marksman', 
      name: 'Marksman', 
      rarity: '6.5%',
      effects: { range: 1.3 },
      description: '+30% Range boost',
      color: 'text-blue-400',
      bgColor: 'bg-blue-500/20',
      borderColor: 'border-blue-400'
    },
    { 
      id: 'scholar', 
      name: 'Scholar', 
      rarity: '10%',
      effects: {},
      description: '+50% Experience boost',
      color: 'text-indigo-400',
      bgColor: 'bg-indigo-500/20',
      borderColor: 'border-indigo-400'
    },
    { 
      id: 'vigor', 
      name: 'Vigor', 
      rarity: '26%',
      effects: { damage: 1.05 }, // Using Vigor 1 as base
      description: 'Vigor 1: +5% Damage (Vigor 2: +10%, Vigor 3: +15%)',
      color: 'text-red-400',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-400'
    },
    { 
      id: 'swift', 
      name: 'Swift', 
      rarity: '26%',
      effects: { sps: 0.95 }, // Using Swift 1 as base
      description: 'Swift 1: -5% SPA (Swift 2: -7.5%, Swift 3: -12.5%)',
      color: 'text-pink-400',
      bgColor: 'bg-pink-500/20',
      borderColor: 'border-pink-400'
    },
    { 
      id: 'range', 
      name: 'Range', 
      rarity: '26%',
      effects: { range: 1.05 }, // Using Range 1 as base
      description: 'Range 1: +5% Range (Range 2: +10%, Range 3: +15%)',
      color: 'text-teal-400',
      bgColor: 'bg-teal-500/20',
      borderColor: 'border-teal-400'
    }
  ];

  // State management
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [unitSearch, setUnitSearch] = useState('');
  const [level, setLevel] = useState(1);
  const [upgrade, setUpgrade] = useState(1);
  const [statGradeSelection, setStatGradeSelection] = useState({
    damage: 'B',
    range: 'B',
    sps: 'B',
    placement: 'B'
  });
  const [selectedTrait, setSelectedTrait] = useState(null);
  const [calculatedStats, setCalculatedStats] = useState(null);

  // Filter units based on search
  const filteredUnits = unitsDatabase.filter(unit =>
    unit.name.toLowerCase().includes(unitSearch.toLowerCase())
  );

  // Calculate final stats
  const calculateStats = () => {
    if (!selectedUnit) return null;

    const baseStats = selectedUnit.baseStats;
    const levelMultiplier = 1 + (level - 1) * 0.1; // 10% increase per level
    const upgradeMultiplier = 1 + (upgrade - 1) * 0.05; // 5% increase per upgrade

    let finalStats = {};

    // Apply base calculations with level and upgrade multipliers
    Object.keys(baseStats).forEach(stat => {
      const gradeMultiplier = statGrades[statGradeSelection[stat]].multiplier;
      let statValue = baseStats[stat] * levelMultiplier * upgradeMultiplier * gradeMultiplier;

      // Apply trait effects
      if (selectedTrait) {
        const trait = availableTraits.find(t => t.id === selectedTrait);
        if (trait && trait.effects[stat]) {
          statValue *= trait.effects[stat];
        }
      }

      finalStats[stat] = Math.round(statValue);
    });

    return finalStats;
  };

  // Update calculated stats when any parameter changes
  useEffect(() => {
    setCalculatedStats(calculateStats());
  }, [selectedUnit, level, upgrade, statGradeSelection, selectedTrait]);

  // Handle unit selection
  const handleUnitSelect = (unit) => {
    setSelectedUnit(unit);
    setUnitSearch(unit.name);
    setLevel(1);
    setUpgrade(1);
    setStatGradeSelection({
      damage: 'B',
      range: 'B',
      sps: 'B', 
      placement: 'B'
    });
    setSelectedTrait(null);
  };

  // Handle stat grade change
  const handleStatGradeChange = (statType, grade) => {
    setStatGradeSelection(prev => ({
      ...prev,
      [statType]: grade
    }));
  };

  // Handle trait toggle
  const handleTraitToggle = (traitId) => {
    setSelectedTraits(prev => {
      if (prev.includes(traitId)) {
        return prev.filter(id => id !== traitId);
      } else if (prev.length < 2) { // Max 2 traits
        return [...prev, traitId];
      }
      return prev;
    });
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Secret': return 'text-red-400';
      case 'Mythic': return 'text-purple-400';
      case 'Legendary': return 'text-yellow-400';
      case 'Epic': return 'text-blue-400';
      default: return 'text-anime-secondary';
    }
  };

  return (
    <div className="min-h-screen py-24 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl md:text-6xl font-black gradient-text mb-6">Stat Calculator</h1>
          <p className="text-xl text-anime-light-gray max-w-3xl mx-auto">
            Select a unit and experiment with <span className="text-anime-primary">stat grades</span>, 
            <span className="text-anime-secondary"> traits</span>, and 
            <span className="text-anime-accent"> levels</span> to optimize your build
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Unit Selection & Configuration Panel */}
          <div className="lg:col-span-2 space-y-8">
            {/* Unit Selection */}
            <div className="anime-card">
              <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                <div className="w-3 h-3 rounded-full bg-anime-primary mr-3"></div>
                Select Unit
              </h2>
              
              <div className="relative mb-6">
                <input
                  type="text"
                  placeholder="Search for units..."
                  value={unitSearch}
                  onChange={(e) => setUnitSearch(e.target.value)}
                  className="w-full bg-anime-gray border border-anime-gray-light rounded-xl px-4 py-3 text-anime-light placeholder-anime-light-gray focus:border-anime-primary focus:outline-none transition-colors"
                />
                
                {unitSearch && !selectedUnit && (
                  <div className="absolute top-full left-0 right-0 bg-anime-gray border border-anime-gray-light rounded-xl mt-2 max-h-64 overflow-y-auto z-10">
                    {filteredUnits.map(unit => (
                      <div
                        key={unit.id}
                        onClick={() => handleUnitSelect(unit)}
                        className="px-4 py-3 hover:bg-anime-gray-light cursor-pointer transition-colors border-b border-anime-gray-light last:border-b-0"
                      >
                        <div className="flex justify-between items-center">
                          <span className="text-anime-light font-medium">{unit.name}</span>
                          <span className={`text-sm ${getRarityColor(unit.rarity)}`}>{unit.rarity}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {selectedUnit && (
                <div className="bg-anime-gray-light rounded-xl p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-bold text-anime-light">{selectedUnit.name}</h3>
                    <div className="flex items-center space-x-2">
                      <span className={`px-3 py-1 rounded-lg text-sm font-bold ${getRarityColor(selectedUnit.rarity)}`}>
                        {selectedUnit.rarity}
                      </span>
                      <span className="px-3 py-1 rounded-lg text-sm font-bold text-anime-accent bg-anime-accent/20">
                        {selectedUnit.element}
                      </span>
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    {Object.entries(selectedUnit.baseStats).map(([stat, value]) => (
                      <div key={stat} className="bg-anime-gray rounded-lg p-3 text-center">
                        <div className="text-anime-light-gray text-sm uppercase tracking-wider">{stat}</div>
                        <div className="text-anime-primary font-bold text-lg">{value}</div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {selectedUnit && (
              <>
                {/* Level & Upgrade Controls */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-secondary mr-3"></div>
                    Level & Upgrade
                  </h2>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-anime-light font-medium mb-3">
                        Level: <span className="text-anime-primary font-bold">{level}</span>
                      </label>
                      <input
                        type="range"
                        min="1"
                        max="100"
                        value={level}
                        onChange={(e) => setLevel(parseInt(e.target.value))}
                        className="w-full h-2 bg-anime-gray rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-anime-light-gray mt-1">
                        <span>1</span>
                        <span>100</span>
                      </div>
                    </div>
                    
                    <div>
                      <label className="block text-anime-light font-medium mb-3">
                        Upgrade: <span className="text-anime-accent font-bold">{upgrade}</span>
                      </label>
                      <input
                        type="range"
                        min="1"
                        max="10"
                        value={upgrade}
                        onChange={(e) => setUpgrade(parseInt(e.target.value))}
                        className="w-full h-2 bg-anime-gray rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-anime-light-gray mt-1">
                        <span>1</span>
                        <span>10</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Stat Grades */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-accent mr-3"></div>
                    Stat Grades
                  </h2>
                  
                  <div className="space-y-6">
                    {Object.keys(selectedUnit.baseStats).map(stat => (
                      <div key={stat}>
                        <label className="block text-anime-light font-medium mb-3 capitalize">
                          {stat} Grade: <span className={`font-bold ${statGrades[statGradeSelection[stat]].color}`}>
                            {statGradeSelection[stat]}
                          </span>
                        </label>
                        <div className="grid grid-cols-4 md:grid-cols-8 gap-2">
                          {Object.keys(statGrades).map(grade => (
                            <button
                              key={grade}
                              onClick={() => handleStatGradeChange(stat, grade)}
                              className={`px-3 py-2 rounded-lg font-bold text-sm transition-all ${
                                statGradeSelection[stat] === grade
                                  ? `${statGrades[grade].color} ${statGrades[grade].bgColor} border-2`
                                  : 'text-anime-light-gray bg-anime-gray hover:bg-anime-gray-light border-2 border-transparent'
                              }`}
                              style={{
                                borderColor: statGradeSelection[stat] === grade ? 
                                  statGrades[grade].color.replace('text-', '') : 'transparent'
                              }}
                            >
                              {grade}
                            </button>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Traits Selection */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-gold mr-3"></div>
                    Traits <span className="text-sm text-anime-light-gray font-normal ml-2">
                      (Select up to 2: {selectedTraits.length}/2)
                    </span>
                  </h2>
                  
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
                    {availableTraits.map(trait => (
                      <button
                        key={trait.id}
                        onClick={() => handleTraitToggle(trait.id)}
                        disabled={!selectedTraits.includes(trait.id) && selectedTraits.length >= 2}
                        className={`p-4 rounded-xl border-2 transition-all text-left ${
                          selectedTraits.includes(trait.id)
                            ? `${trait.color} bg-current/10 border-current`
                            : 'text-anime-light-gray bg-anime-gray border-anime-gray-light hover:border-anime-primary disabled:opacity-50 disabled:cursor-not-allowed'
                        }`}
                      >
                        <div className="font-bold text-sm">{trait.name}</div>
                        <div className="text-xs opacity-75 capitalize">{trait.effect} +{((trait.multiplier - 1) * 100).toFixed(0)}%</div>
                      </button>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>

          {/* Results Panel */}
          <div className="lg:col-span-1">
            <div className="anime-card sticky top-24">
              <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                <div className="w-3 h-3 rounded-full bg-anime-neon mr-3"></div>
                Final Stats
              </h2>
              
              {selectedUnit && calculatedStats ? (
                <div className="space-y-4">
                  {Object.entries(calculatedStats).map(([stat, value]) => (
                    <div key={stat} className="bg-anime-gray-light rounded-xl p-4">
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-anime-light-gray text-sm uppercase tracking-wider">{stat}</span>
                        <span className={`text-sm font-bold ${statGrades[statGradeSelection[stat]].color}`}>
                          {statGradeSelection[stat]}
                        </span>
                      </div>
                      <div className="flex justify-between items-end">
                        <div>
                          <div className="text-2xl font-black text-anime-primary">{value.toLocaleString()}</div>
                          <div className="text-xs text-anime-light-gray">
                            Base: {selectedUnit.baseStats[stat].toLocaleString()}
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="text-sm font-bold text-anime-accent">
                            +{(((value / selectedUnit.baseStats[stat]) - 1) * 100).toFixed(1)}%
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                  
                  {selectedTraits.length > 0 && (
                    <div className="border-t border-anime-gray-light pt-4 mt-6">
                      <h3 className="text-anime-light font-bold mb-3">Active Traits</h3>
                      <div className="space-y-2">
                        {selectedTraits.map(traitId => {
                          const trait = availableTraits.find(t => t.id === traitId);
                          return (
                            <div key={traitId} className="flex justify-between text-sm">
                              <span className={trait.color}>{trait.name}</span>
                              <span className="text-anime-light-gray">
                                {trait.effect} +{((trait.multiplier - 1) * 100).toFixed(0)}%
                              </span>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-anime-gray rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <div className="w-8 h-8 bg-anime-primary/30 rounded-lg"></div>
                  </div>
                  <p className="text-anime-light-gray">Select a unit to start calculating stats</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
