import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="fixed top-0 w-full z-50 glass-effect border-b border-anime-primary/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3 group">
            <div className="relative">
              <div className="w-12 h-12 bg-anime-gradient rounded-xl flex items-center justify-center transition-all duration-300 group-hover:scale-110 group-hover:rotate-3">
                <span className="text-white font-bold text-xl">AV</span>
              </div>
              <div className="absolute inset-0 rounded-xl bg-anime-gradient opacity-50 blur-lg group-hover:opacity-75 transition-opacity duration-300"></div>
            </div>
            <span className="gradient-text text-2xl font-bold hidden sm:block tracking-tight">
              Anime Vanguards
            </span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex space-x-8">
            <Link
              to="/"
              className={`anime-nav-link ${
                isActive('/') ? 'text-anime-primary' : ''
              }`}
            >
              Home
            </Link>
            <Link
              to="/calculator"
              className={`anime-nav-link ${
                isActive('/calculator') ? 'text-anime-primary' : ''
              }`}
            >
              Calculator
            </Link>
            <Link
              to="/units"
              className={`anime-nav-link ${
                isActive('/units') ? 'text-anime-primary' : ''
              }`}
            >
              Unit List
            </Link>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-anime-light hover:text-anime-primary transition-colors duration-200"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isOpen ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                )}
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isOpen && (
          <div className="md:hidden py-4 border-t border-anime-gray-light/20">
            <div className="flex flex-col space-y-4">
              <Link
                to="/"
                className={`anime-nav-link ${
                  isActive('/') ? 'text-anime-primary' : ''
                }`}
                onClick={() => setIsOpen(false)}
              >
                Home
              </Link>
              <Link
                to="/calculator"
                className={`anime-nav-link ${
                  isActive('/calculator') ? 'text-anime-primary' : ''
                }`}
                onClick={() => setIsOpen(false)}
              >
                Calculator
              </Link>
              <Link
                to="/units"
                className={`anime-nav-link ${
                  isActive('/units') ? 'text-anime-primary' : ''
                }`}
                onClick={() => setIsOpen(false)}
              >
                Unit List
              </Link>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
