import React, { useState, useEffect } from 'react';

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythical',
      element: 'Holy',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythical',
      element: 'Fire',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Curse',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1050,
        range: 8.0,
        sps: 1.3,
        placement: 29500
      }
    },
    {
      id: 5,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Blast',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1650,
        range: 6.5,
        sps: 0.8,
        placement: 45000
      }
    },
    {
      id: 6,
      name: 'Gojo Satoru',
      rarity: 'Mythical',
      element: 'Cosmic',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1320,
        range: 10.5,
        sps: 1.0,
        placement: 38000
      }
    },
    {
      id: 7,
      name: 'Naruto Baryon',
      rarity: 'Exclusive',
      element: 'Nature',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1800,
        range: 7.0,
        sps: 0.9,
        placement: 55000
      }
    },
    {
      id: 8,
      name: 'Luffy Gear 5',
      rarity: 'Secret',
      element: 'Passion',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1450,
        range: 6.8,
        sps: 1.1,
        placement: 42000
      }
    }
  ];

  // Stat grades and their percentage ranges
  const statGrades = {
    'GODLY': { 
      damageRange: [23, 25], spaRange: [11.5, 12.5], rangeRange: [11.5, 12.5], 
      color: 'text-cyan-400', bgColor: 'bg-cyan-500/20', chance: '0.1%', potential: '0.25%' 
    },
    'Z+': { 
      damageRange: [20, 22.99], spaRange: [10, 11.49], rangeRange: [10, 11.49], 
      color: 'text-pink-400', bgColor: 'bg-pink-500/20', chance: '0.2%', potential: '0.6%' 
    },
    'Z': { 
      damageRange: [17, 19.99], spaRange: [8.5, 9.9], rangeRange: [8.5, 9.9], 
      color: 'text-yellow-400', bgColor: 'bg-yellow-500/20', chance: '0.85%', potential: '4.15%' 
    },
    'S': { 
      damageRange: [14, 16.99], spaRange: [7, 8.49], rangeRange: [7, 8.49], 
      color: 'text-purple-400', bgColor: 'bg-purple-500/20', chance: '1%', potential: '20%' 
    },
    'A': { 
      damageRange: [11, 13.99], spaRange: [5.5, 6.99], rangeRange: [5.5, 6.99], 
      color: 'text-blue-400', bgColor: 'bg-blue-500/20', chance: '4.35%', potential: '75%' 
    },
    'B': { 
      damageRange: [8, 10.99], spaRange: [4, 5.49], rangeRange: [4, 5.49], 
      color: 'text-green-400', bgColor: 'bg-green-500/20', chance: '30%', potential: '0%' 
    },
    'C': { 
      damageRange: [5, 7.99], spaRange: [2.5, 3.99], rangeRange: [2.5, 3.99], 
      color: 'text-gray-400', bgColor: 'bg-gray-500/20', chance: '30%', potential: '0%' 
    },
    'D': { 
      damageRange: [0, 4.99], spaRange: [0, 2.49], rangeRange: [0, 2.49], 
      color: 'text-red-400', bgColor: 'bg-red-500/20', chance: '40%', potential: '0%' 
    }
  };

  // Available traits and their effects
  const availableTraits = [
    { 
      id: 'monarch', 
      name: 'Monarch', 
      rarity: '0.1%',
      effects: { damage: 4.0, sps: 0.9, range: 1.05 },
      description: '+300% Damage, -10% SPA, +5% Range, ONE PLACEMENT only',
      color: 'text-red-500',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-500'
    },
    { 
      id: 'ethereal', 
      name: 'Ethereal', 
      rarity: '0.175%',
      effects: { damage: 1.2, sps: 0.8, range: 1.05 },
      description: '+20% Damage, -20% SPA, +5% Range',
      color: 'text-purple-400',
      bgColor: 'bg-purple-500/20',
      borderColor: 'border-purple-400'
    },
    { 
      id: 'deadeye', 
      name: 'Deadeye', 
      rarity: '0.375%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-orange-400',
      bgColor: 'bg-orange-500/20',
      borderColor: 'border-orange-400'
    },
    { 
      id: 'solar', 
      name: 'Solar', 
      rarity: '0.5%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-yellow-400',
      bgColor: 'bg-yellow-500/20',
      borderColor: 'border-yellow-400'
    },
    { 
      id: 'blitz', 
      name: 'Blitz', 
      rarity: '1.85%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction',
      color: 'text-cyan-400',
      bgColor: 'bg-cyan-500/20',
      borderColor: 'border-cyan-400'
    },
    { 
      id: 'fortune', 
      name: 'Fortune', 
      rarity: '2.5%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction, +20% Cash (Farm), -10% Upgrade cost (Non-farm)',
      color: 'text-green-400',
      bgColor: 'bg-green-500/20',
      borderColor: 'border-green-400'
    },
    { 
      id: 'marksman', 
      name: 'Marksman', 
      rarity: '6.5%',
      effects: { range: 1.3 },
      description: '+30% Range boost',
      color: 'text-blue-400',
      bgColor: 'bg-blue-500/20',
      borderColor: 'border-blue-400'
    },
    { 
      id: 'scholar', 
      name: 'Scholar', 
      rarity: '10%',
      effects: {},
      description: '+50% Experience boost',
      color: 'text-indigo-400',
      bgColor: 'bg-indigo-500/20',
      borderColor: 'border-indigo-400'
    },
    { 
      id: 'vigor', 
      name: 'Vigor', 
      rarity: '26%',
      effects: { damage: 1.05 }, // Using Vigor 1 as base
      description: 'Vigor 1: +5% Damage (Vigor 2: +10%, Vigor 3: +15%)',
      color: 'text-red-400',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-400'
    },
    { 
      id: 'swift', 
      name: 'Swift', 
      rarity: '26%',
      effects: { sps: 0.95 }, // Using Swift 1 as base
      description: 'Swift 1: -5% SPA (Swift 2: -7.5%, Swift 3: -12.5%)',
      color: 'text-pink-400',
      bgColor: 'bg-pink-500/20',
      borderColor: 'border-pink-400'
    },
    { 
      id: 'range', 
      name: 'Range', 
      rarity: '26%',
      effects: { range: 1.05 }, // Using Range 1 as base
      description: 'Range 1: +5% Range (Range 2: +10%, Range 3: +15%)',
      color: 'text-teal-400',
      bgColor: 'bg-teal-500/20',
      borderColor: 'border-teal-400'
    }
  ];

  // State management
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [unitSearch, setUnitSearch] = useState('');
  const [level, setLevel] = useState(1);
  const [upgrade, setUpgrade] = useState(1);
  const [statGradeSelection, setStatGradeSelection] = useState({
    damage: 'B',
    range: 'B',
    sps: 'B',
    placement: 'B'
  });
  const [statPercentages, setStatPercentages] = useState({
    damage: 9,
    range: 4.75,
    sps: 4.75,
    placement: 9
  });
  const [selectedTrait, setSelectedTrait] = useState(null);
  const [calculatedStats, setCalculatedStats] = useState(null);

  // Filter units based on search
  const filteredUnits = unitsDatabase.filter(unit =>
    unit.name.toLowerCase().includes(unitSearch.toLowerCase())
  );

  // Helper function to get stat range key
  const getStatRangeKey = (stat) => {
    if (stat === 'damage') return 'damageRange';
    if (stat === 'sps') return 'spaRange';
    if (stat === 'range') return 'rangeRange';
    if (stat === 'placement') return 'damageRange'; // Placement uses damage range
    return 'damageRange';
  };

  // Calculate final stats
  const calculateStats = () => {
    if (!selectedUnit) return null;

    const baseStats = selectedUnit.baseStats;
    const levelMultiplier = 1 + (level - 1) * 0.1; // 10% increase per level
    const upgradeMultiplier = 1 + (upgrade - 1) * 0.05; // 5% increase per upgrade

    let finalStats = {};

    // Apply base calculations with level and upgrade multipliers
    Object.keys(baseStats).forEach(stat => {
      // Use percentage-based multiplier instead of fixed grade multiplier
      const percentageBonus = statPercentages[stat] / 100;
      const statMultiplier = 1 + percentageBonus;
      let statValue = baseStats[stat] * levelMultiplier * upgradeMultiplier * statMultiplier;

      // Apply trait effects
      if (selectedTrait) {
        const trait = availableTraits.find(t => t.id === selectedTrait);
        if (trait && trait.effects[stat]) {
          statValue *= trait.effects[stat];
        }
      }

      finalStats[stat] = Math.round(statValue);
    });

    return finalStats;
  };

  // Update calculated stats when any parameter changes
  useEffect(() => {
    setCalculatedStats(calculateStats());
  }, [selectedUnit, level, upgrade, statGradeSelection, selectedTrait]);

  // Handle unit selection
  const handleUnitSelect = (unit) => {
    setSelectedUnit(unit);
    setUnitSearch(unit.name);
    setLevel(1);
    setUpgrade(1);
    setStatGradeSelection({
      damage: 'B',
      range: 'B',
      sps: 'B',
      placement: 'B'
    });
    setSelectedTrait(null);
  };

  // Handle stat grade change
  const handleStatGradeChange = (statType, grade) => {
    setStatGradeSelection(prev => ({
      ...prev,
      [statType]: grade
    }));
  };

  // Handle trait selection
  const handleTraitSelect = (traitId) => {
    setSelectedTrait(prev => prev === traitId ? null : traitId);
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'text-red-600';
      case 'Exclusive': return 'text-pink-400';
      case 'Secret': return 'text-red-400';
      case 'Mythical': return 'text-purple-400';
      case 'Legendary': return 'text-yellow-400';
      case 'Epic': return 'text-blue-400';
      case 'Rare': return 'text-green-400';
      default: return 'text-anime-secondary';
    }
  };

  const getRarityBorder = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'border-red-600';
      case 'Exclusive': return 'border-pink-400';
      case 'Secret': return 'border-red-400';
      case 'Mythical': return 'border-purple-400';
      case 'Legendary': return 'border-yellow-400';
      case 'Epic': return 'border-blue-400';
      case 'Rare': return 'border-green-400';
      default: return 'border-anime-secondary';
    }
  };

  const getRarityBg = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'bg-red-600/20';
      case 'Exclusive': return 'bg-pink-400/20';
      case 'Secret': return 'bg-red-400/20';
      case 'Mythical': return 'bg-purple-400/20';
      case 'Legendary': return 'bg-yellow-400/20';
      case 'Epic': return 'bg-blue-400/20';
      case 'Rare': return 'bg-green-400/20';
      default: return 'bg-anime-secondary/20';
    }
  };

  return (
    <div className="min-h-screen py-24 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl md:text-6xl font-black gradient-text mb-6">Stat Calculator</h1>
          <p className="text-xl text-anime-light-gray max-w-3xl mx-auto">
            Select a unit and experiment with <span className="text-anime-primary">stat grades</span>, 
            <span className="text-anime-secondary"> traits</span>, and 
            <span className="text-anime-accent"> levels</span> to optimize your build
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Unit Selection & Configuration Panel */}
          <div className="lg:col-span-2 space-y-8">
            {/* Unit Selection */}
            <div className="anime-card">
              <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                <div className="w-3 h-3 rounded-full bg-anime-primary mr-3"></div>
                Select Unit
              </h2>
              
              {/* Search Input - Only show when no unit is selected */}
              {!selectedUnit && (
                <div className="relative mb-6">
                  <input
                    type="text"
                    placeholder="Search for units..."
                    value={unitSearch}
                    onChange={(e) => setUnitSearch(e.target.value)}
                    className="w-full bg-anime-gray border border-anime-gray-light rounded-xl px-4 py-3 text-anime-light placeholder-anime-light-gray focus:border-anime-primary focus:outline-none transition-colors"
                  />
                </div>
              )}

              {/* Unit Cards Grid - Only show when no unit is selected */}
              {!selectedUnit && (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 mb-6">
                {filteredUnits.map(unit => (
                  <button
                    key={unit.id}
                    onClick={() => handleUnitSelect(unit)}
                    className={`p-4 rounded-xl border-2 transition-all text-left group relative overflow-hidden ${
                      selectedUnit?.id === unit.id
                        ? `${getRarityColor(unit.rarity)} ${getRarityBg(unit.rarity)} ${getRarityBorder(unit.rarity)}`
                        : 'text-anime-light-gray bg-anime-gray border-anime-gray-light hover:border-anime-primary hover:bg-anime-gray-light'
                    }`}
                  >
                    {/* Unit Image */}
                    <div className="w-full h-32 bg-anime-gray-light rounded-lg mb-3 overflow-hidden relative">
                      <img 
                        src={unit.image} 
                        alt={unit.name}
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <div className="w-full h-full bg-anime-primary/30 rounded-lg hidden items-center justify-center">
                        <div className="w-8 h-8 bg-anime-primary rounded"></div>
                      </div>
                      
                      {/* Rarity Badge */}
                      <div className="absolute top-2 right-2">
                        <span className={`px-2 py-1 rounded-lg text-xs font-bold ${getRarityColor(unit.rarity)} bg-anime-darker/90`}>
                          {unit.rarity}
                        </span>
                      </div>
                    </div>
                    
                    {/* Unit Info */}
                    <div className="space-y-2">
                      <h3 className="font-bold text-sm truncate">{unit.name}</h3>
                      
                      {/* Element and Tier */}
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-1">
                          <div className="w-5 h-5 flex items-center justify-center">
                            <img 
                              src={`/images/Elements/${unit.element}${unit.element === 'Holy' ? ' (1)' : ''}.webp`} 
                              alt={unit.element}
                              className="w-full h-full object-contain"
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'block';
                              }}
                            />
                            <div className="w-3 h-3 bg-anime-accent/50 rounded hidden"></div>
                          </div>
                          <span className="text-xs text-anime-light-gray">{unit.element}</span>
                        </div>
                        
                        <div className="flex items-center space-x-1">
                          <div className="w-5 h-5 flex items-center justify-center">
                            <img 
                              src={`/images/UnitTiers/${unit.tier}.webp`} 
                              alt={unit.tier}
                              className="w-full h-full object-contain"
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'block';
                              }}
                            />
                            <div className="w-3 h-3 bg-anime-secondary/50 rounded hidden"></div>
                          </div>
                          <span className="text-xs text-anime-light-gray">{unit.tier}</span>
                        </div>
                      </div>
                    </div>
                    
                    {/* Selection Indicator */}
                    {selectedUnit?.id === unit.id && (
                      <div className="absolute top-2 left-2 w-6 h-6 bg-anime-primary rounded-full flex items-center justify-center">
                        <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                        </svg>
                      </div>
                    )}
                  </button>
                ))}
                </div>
              )}

              {/* Selected Unit Details */}
              {selectedUnit && (
                <div className={`bg-anime-gray-light rounded-xl p-6 border-2 ${getRarityBorder(selectedUnit.rarity)}`}>
                  {/* Change Unit Button */}
                  <div className="flex justify-between items-center mb-6">
                    <h3 className="text-lg font-bold text-anime-light">Selected Unit</h3>
                    <button
                      onClick={() => {
                        setSelectedUnit(null);
                        setUnitSearch('');
                        setLevel(1);
                        setUpgrade(1);
                        setStatGradeSelection({
                          damage: 'B',
                          range: 'B',
                          sps: 'B',
                          placement: 'B'
                        });
                        setSelectedTrait(null);
                      }}
                      className="px-4 py-2 bg-anime-primary text-white rounded-lg hover:bg-anime-blue-light transition-colors text-sm font-medium"
                    >
                      Change Unit
                    </button>
                  </div>
                  
                  <div className="flex items-start space-x-6 mb-6">
                    {/* Large Unit Image */}
                    <div className="w-24 h-24 bg-anime-gray rounded-xl overflow-hidden flex-shrink-0">
                      <img 
                        src={selectedUnit.image} 
                        alt={selectedUnit.name}
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <div className="w-full h-full bg-anime-primary/30 rounded-xl hidden items-center justify-center">
                        <div className="w-10 h-10 bg-anime-primary rounded"></div>
                      </div>
                    </div>
                    
                    {/* Unit Details */}
                    <div className="flex-1">
                      <h3 className="text-2xl font-bold text-anime-light mb-3">{selectedUnit.name}</h3>
                      
                      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                        {/* Rarity */}
                        <div className="flex items-center space-x-2">
                          <span className="text-anime-light-gray text-sm">Rarity:</span>
                          <span className={`px-3 py-1 rounded-lg text-sm font-bold ${getRarityColor(selectedUnit.rarity)} ${getRarityBg(selectedUnit.rarity)}`}>
                            {selectedUnit.rarity}
                          </span>
                        </div>
                        
                        {/* Element */}
                        <div className="flex items-center space-x-2">
                          <span className="text-anime-light-gray text-sm">Element:</span>
                          <div className="flex items-center space-x-2 px-3 py-1 rounded-lg bg-anime-accent/20">
                            <div className="w-5 h-5 flex items-center justify-center">
                              <img 
                                src={`/images/Elements/${selectedUnit.element}${selectedUnit.element === 'Holy' ? ' (1)' : ''}.webp`} 
                                alt={selectedUnit.element}
                                className="w-full h-full object-contain"
                                onError={(e) => {
                                  e.target.style.display = 'none';
                                  e.target.nextSibling.style.display = 'block';
                                }}
                              />
                              <div className="w-3 h-3 bg-anime-accent/50 rounded hidden"></div>
                            </div>
                            <span className="text-sm font-bold text-anime-accent">{selectedUnit.element}</span>
                          </div>
                        </div>
                        
                        {/* Tier */}
                        <div className="flex items-center space-x-2">
                          <span className="text-anime-light-gray text-sm">Tier:</span>
                          <div className="flex items-center space-x-2 px-3 py-1 rounded-lg bg-anime-secondary/20">
                            <div className="w-5 h-5 flex items-center justify-center">
                              <img 
                                src={`/images/UnitTiers/${selectedUnit.tier}.webp`} 
                                alt={selectedUnit.tier}
                                className="w-full h-full object-contain"
                                onError={(e) => {
                                  e.target.style.display = 'none';
                                  e.target.nextSibling.style.display = 'block';
                                }}
                              />
                              <div className="w-3 h-3 bg-anime-secondary/50 rounded hidden"></div>
                            </div>
                            <span className="text-sm font-bold text-anime-secondary">{selectedUnit.tier}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  {/* Base Stats */}
                  <div>
                    <h4 className="text-lg font-bold text-anime-light mb-3">Base Stats</h4>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                      {Object.entries(selectedUnit.baseStats).map(([stat, value]) => (
                        <div key={stat} className="bg-anime-gray rounded-lg p-4 text-center">
                          <div className="flex flex-col items-center space-y-2">
                            <div className="text-anime-light-gray text-sm uppercase tracking-wider font-medium">{stat}</div>
                            <div className="text-anime-primary font-bold text-lg">{value}</div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>

            {selectedUnit && (
              <>
                {/* Level & Upgrade Controls */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-secondary mr-3"></div>
                    Level & Upgrade
                  </h2>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-anime-light font-medium mb-3">
                        Level: <span className="text-anime-primary font-bold">{level}</span>
                      </label>
                      <input
                        type="range"
                        min="1"
                        max="100"
                        value={level}
                        onChange={(e) => setLevel(parseInt(e.target.value))}
                        className="w-full h-2 bg-anime-gray rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-anime-light-gray mt-1">
                        <span>1</span>
                        <span>100</span>
                      </div>
                    </div>
                    
                    <div>
                      <label className="block text-anime-light font-medium mb-3">
                        Upgrade: <span className="text-anime-accent font-bold">{upgrade}</span>
                      </label>
                      <input
                        type="range"
                        min="1"
                        max="10"
                        value={upgrade}
                        onChange={(e) => setUpgrade(parseInt(e.target.value))}
                        className="w-full h-2 bg-anime-gray rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-anime-light-gray mt-1">
                        <span>1</span>
                        <span>10</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Stat Grades */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-accent mr-3"></div>
                    Stat Grades
                  </h2>
                  
                  <div className="space-y-6">
                    {Object.keys(selectedUnit.baseStats).map(stat => (
                      <div key={stat}>
                        <label className="block text-anime-light font-medium mb-3 capitalize">
                          {stat} Grade: <span className={`font-bold ${statGrades[statGradeSelection[stat]].color}`}>
                            {statGradeSelection[stat]}
                          </span>
                        </label>
                        <div className="grid grid-cols-4 md:grid-cols-8 gap-2">
                          {Object.keys(statGrades).map(grade => (
                            <button
                              key={grade}
                              onClick={() => handleStatGradeChange(stat, grade)}
                              className={`p-3 rounded-lg font-bold text-sm transition-all flex flex-col items-center space-y-1 ${
                                statGradeSelection[stat] === grade
                                  ? `${statGrades[grade].color} ${statGrades[grade].bgColor} border-2`
                                  : 'text-anime-light-gray bg-anime-gray hover:bg-anime-gray-light border-2 border-transparent'
                              }`}
                              style={{
                                borderColor: statGradeSelection[stat] === grade ? 
                                  statGrades[grade].color.replace('text-', '') : 'transparent'
                              }}
                            >
                              <div className="w-6 h-6 flex items-center justify-center">
                                <img 
                                  src={`/images/Stats/Stat${grade === 'Z+' ? 'ZPLUS' : grade}.webp`} 
                                  alt={grade}
                                  className="w-full h-full object-contain"
                                  onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.nextSibling.style.display = 'block';
                                  }}
                                />
                                <div className="w-4 h-4 bg-anime-primary/30 rounded hidden"></div>
                              </div>
                              <span className="text-xs">{grade}</span>
                            </button>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Traits Selection */}
                <div className="anime-card">
                  <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                    <div className="w-3 h-3 rounded-full bg-anime-gold mr-3"></div>
                    Traits <span className="text-sm text-anime-light-gray font-normal ml-2">
                      (Select 1 trait)
                    </span>
                  </h2>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {availableTraits.map(trait => (
                      <button
                        key={trait.id}
                        onClick={() => handleTraitSelect(trait.id)}
                        className={`p-5 rounded-xl border-2 transition-all text-left group relative overflow-hidden ${
                          selectedTrait === trait.id
                            ? `${trait.color} ${trait.bgColor} ${trait.borderColor}`
                            : 'text-anime-light-gray bg-anime-gray border-anime-gray-light hover:border-anime-primary hover:bg-anime-gray-light'
                        }`}
                      >
                        {/* Trait Image */}
                        <div className="flex items-start space-x-4 mb-3">
                          <div className="w-12 h-12 flex items-center justify-center flex-shrink-0">
                            <img 
                              src={`/images/Traits/${trait.name}.webp`} 
                              alt={trait.name}
                              className="w-full h-full object-contain"
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'flex';
                              }}
                            />
                            <div className="w-8 h-8 bg-anime-primary/30 rounded-lg hidden items-center justify-center">
                              <div className="w-4 h-4 bg-anime-primary rounded"></div>
                            </div>
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-1">
                              <div className="font-bold text-base">{trait.name}</div>
                              <div className="text-xs opacity-75 font-medium">{trait.rarity}</div>
                            </div>
                            <div className="text-xs opacity-90 leading-relaxed">{trait.description}</div>
                          </div>
                        </div>
                        
                        {/* Selection Indicator */}
                        {selectedTrait === trait.id && (
                          <div className="absolute top-2 right-2 w-6 h-6 bg-anime-primary rounded-full flex items-center justify-center">
                            <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                              <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                            </svg>
                          </div>
                        )}
                      </button>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>

          {/* Results Panel */}
          <div className="lg:col-span-1">
            <div className="anime-card sticky top-24">
              <h2 className="text-2xl font-bold text-anime-light mb-6 flex items-center">
                <div className="w-3 h-3 rounded-full bg-anime-neon mr-3"></div>
                Final Stats
              </h2>
              
              {selectedUnit && calculatedStats ? (
                <div className="space-y-4">
                  {Object.entries(calculatedStats).map(([stat, value]) => (
                    <div key={stat} className="bg-anime-gray-light rounded-xl p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center space-x-3">
                          <span className="text-anime-light-gray text-sm uppercase tracking-wider font-medium">{stat}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <div className="w-5 h-5 flex items-center justify-center">
                            <img 
                              src={`/images/Stats/Stat${statGradeSelection[stat] === 'Z+' ? 'ZPLUS' : statGradeSelection[stat]}.webp`} 
                              alt={statGradeSelection[stat]}
                              className="w-full h-full object-contain"
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'block';
                              }}
                            />
                            <div className="w-3 h-3 bg-anime-primary/30 rounded hidden"></div>
                          </div>
                          <span className={`text-sm font-bold ${statGrades[statGradeSelection[stat]].color}`}>
                            {statGradeSelection[stat]}
                          </span>
                        </div>
                      </div>
                      <div className="flex justify-between items-end">
                        <div>
                          <div className="text-2xl font-black text-anime-primary">{value.toLocaleString()}</div>
                          <div className="text-xs text-anime-light-gray">
                            Base: {selectedUnit.baseStats[stat].toLocaleString()}
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="text-sm font-bold text-anime-accent">
                            +{(((value / selectedUnit.baseStats[stat]) - 1) * 100).toFixed(1)}%
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                  
                  {selectedTrait && (
                    <div className="border-t border-anime-gray-light pt-4 mt-6">
                      <h3 className="text-anime-light font-bold mb-3">Active Trait</h3>
                      <div className="bg-anime-gray-light rounded-xl p-4">
                        {(() => {
                          const trait = availableTraits.find(t => t.id === selectedTrait);
                          return (
                            <div className="flex items-center space-x-3">
                              <div className="w-10 h-10 flex items-center justify-center flex-shrink-0">
                                <img 
                                  src={`/images/Traits/${trait.name}.webp`} 
                                  alt={trait.name}
                                  className="w-full h-full object-contain"
                                  onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.nextSibling.style.display = 'flex';
                                  }}
                                />
                                <div className="w-8 h-8 bg-anime-primary/30 rounded-lg hidden items-center justify-center">
                                  <div className="w-4 h-4 bg-anime-primary rounded"></div>
                                </div>
                              </div>
                              <div className="flex-1">
                                <div className={`font-bold ${trait.color}`}>{trait.name}</div>
                                <div className="text-xs text-anime-light-gray mt-1">{trait.description}</div>
                              </div>
                            </div>
                          );
                        })()}
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-anime-gray rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <div className="w-8 h-8 bg-anime-primary/30 rounded-lg"></div>
                  </div>
                  <p className="text-anime-light-gray">Select a unit to start calculating stats</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
