import React, { useState, useEffect } from 'react';

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythical',
      element: 'Holy',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythical',
      element: 'Fire',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Curse',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1050,
        range: 8.0,
        sps: 1.3,
        placement: 29500
      }
    },
    {
      id: 5,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Blast',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1650,
        range: 6.5,
        sps: 0.8,
        placement: 45000
      }
    },
    {
      id: 6,
      name: 'Gojo Satoru',
      rarity: 'Mythical',
      element: 'Cosmic',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1320,
        range: 10.5,
        sps: 1.0,
        placement: 38000
      }
    },
    {
      id: 7,
      name: 'Naruto Baryon',
      rarity: 'Exclusive',
      element: 'Nature',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1800,
        range: 7.0,
        sps: 0.9,
        placement: 55000
      }
    },
    {
      id: 8,
      name: 'Luffy Gear 5',
      rarity: 'Secret',
      element: 'Passion',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1450,
        range: 6.8,
        sps: 1.1,
        placement: 42000
      }
    }
  ];

  // Stat grades and their percentage ranges
  const statGrades = {
    'GODLY': { 
      damageRange: [23, 25], spaRange: [11.5, 12.5], rangeRange: [11.5, 12.5], 
      color: 'text-cyan-400', bgColor: 'bg-cyan-500/20', chance: '0.1%', potential: '0.25%' 
    },
    'Z+': { 
      damageRange: [20, 22.99], spaRange: [10, 11.49], rangeRange: [10, 11.49], 
      color: 'text-pink-400', bgColor: 'bg-pink-500/20', chance: '0.2%', potential: '0.6%' 
    },
    'Z': { 
      damageRange: [17, 19.99], spaRange: [8.5, 9.9], rangeRange: [8.5, 9.9], 
      color: 'text-yellow-400', bgColor: 'bg-yellow-500/20', chance: '0.85%', potential: '4.15%' 
    },
    'S': { 
      damageRange: [14, 16.99], spaRange: [7, 8.49], rangeRange: [7, 8.49], 
      color: 'text-purple-400', bgColor: 'bg-purple-500/20', chance: '1%', potential: '20%' 
    },
    'A': { 
      damageRange: [11, 13.99], spaRange: [5.5, 6.99], rangeRange: [5.5, 6.99], 
      color: 'text-blue-400', bgColor: 'bg-blue-500/20', chance: '4.35%', potential: '75%' 
    },
    'B': { 
      damageRange: [8, 10.99], spaRange: [4, 5.49], rangeRange: [4, 5.49], 
      color: 'text-green-400', bgColor: 'bg-green-500/20', chance: '30%', potential: '0%' 
    },
    'C': { 
      damageRange: [5, 7.99], spaRange: [2.5, 3.99], rangeRange: [2.5, 3.99], 
      color: 'text-gray-400', bgColor: 'bg-gray-500/20', chance: '30%', potential: '0%' 
    },
    'D': { 
      damageRange: [0, 4.99], spaRange: [0, 2.49], rangeRange: [0, 2.49], 
      color: 'text-red-400', bgColor: 'bg-red-500/20', chance: '40%', potential: '0%' 
    }
  };

  // Available traits and their effects
  const availableTraits = [
    { 
      id: 'monarch', 
      name: 'Monarch', 
      rarity: '0.1%',
      effects: { damage: 4.0, sps: 0.9, range: 1.05 },
      description: '+300% Damage, -10% SPA, +5% Range, ONE PLACEMENT only',
      color: 'text-red-500',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-500'
    },
    { 
      id: 'ethereal', 
      name: 'Ethereal', 
      rarity: '0.175%',
      effects: { damage: 1.2, sps: 0.8, range: 1.05 },
      description: '+20% Damage, -20% SPA, +5% Range',
      color: 'text-purple-400',
      bgColor: 'bg-purple-500/20',
      borderColor: 'border-purple-400'
    },
    { 
      id: 'deadeye', 
      name: 'Deadeye', 
      rarity: '0.375%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-orange-400',
      bgColor: 'bg-orange-500/20',
      borderColor: 'border-orange-400'
    },
    { 
      id: 'solar', 
      name: 'Solar', 
      rarity: '0.5%',
      effects: { damage: 1.675 }, // 45% crit chance + 50% crit damage ≈ 67.5% damage increase
      description: '45% Critical Chance, 50% Critical Damage',
      color: 'text-yellow-400',
      bgColor: 'bg-yellow-500/20',
      borderColor: 'border-yellow-400'
    },
    { 
      id: 'blitz', 
      name: 'Blitz', 
      rarity: '1.85%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction',
      color: 'text-cyan-400',
      bgColor: 'bg-cyan-500/20',
      borderColor: 'border-cyan-400'
    },
    { 
      id: 'fortune', 
      name: 'Fortune', 
      rarity: '2.5%',
      effects: { sps: 0.8 },
      description: '-20% SPA reduction, +20% Cash (Farm), -10% Upgrade cost (Non-farm)',
      color: 'text-green-400',
      bgColor: 'bg-green-500/20',
      borderColor: 'border-green-400'
    },
    { 
      id: 'marksman', 
      name: 'Marksman', 
      rarity: '6.5%',
      effects: { range: 1.3 },
      description: '+30% Range boost',
      color: 'text-blue-400',
      bgColor: 'bg-blue-500/20',
      borderColor: 'border-blue-400'
    },
    { 
      id: 'scholar', 
      name: 'Scholar', 
      rarity: '10%',
      effects: {},
      description: '+50% Experience boost',
      color: 'text-indigo-400',
      bgColor: 'bg-indigo-500/20',
      borderColor: 'border-indigo-400'
    },
    { 
      id: 'vigor', 
      name: 'Vigor', 
      rarity: '26%',
      effects: { damage: 1.05 }, // Using Vigor 1 as base
      description: 'Vigor 1: +5% Damage (Vigor 2: +10%, Vigor 3: +15%)',
      color: 'text-red-400',
      bgColor: 'bg-red-500/20',
      borderColor: 'border-red-400'
    },
    { 
      id: 'swift', 
      name: 'Swift', 
      rarity: '26%',
      effects: { sps: 0.95 }, // Using Swift 1 as base
      description: 'Swift 1: -5% SPA (Swift 2: -7.5%, Swift 3: -12.5%)',
      color: 'text-pink-400',
      bgColor: 'bg-pink-500/20',
      borderColor: 'border-pink-400'
    },
    { 
      id: 'range', 
      name: 'Range', 
      rarity: '26%',
      effects: { range: 1.05 }, // Using Range 1 as base
      description: 'Range 1: +5% Range (Range 2: +10%, Range 3: +15%)',
      color: 'text-teal-400',
      bgColor: 'bg-teal-500/20',
      borderColor: 'border-teal-400'
    }
  ];

  // State management
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [unitSearch, setUnitSearch] = useState('');
  const [level, setLevel] = useState(1);
  const [upgrade, setUpgrade] = useState(1);
  const [statGradeSelection, setStatGradeSelection] = useState({
    damage: 'B',
    range: 'B',
    sps: 'B'
  });
  const [statPercentages, setStatPercentages] = useState({
    damage: 9,
    range: 4.75,
    sps: 4.75
  });
  const [selectedTrait, setSelectedTrait] = useState(null);
  const [calculatedStats, setCalculatedStats] = useState(null);

  // Filter units based on search
  const filteredUnits = unitsDatabase.filter(unit =>
    unit.name.toLowerCase().includes(unitSearch.toLowerCase())
  );

  // Helper function to get stat range key
  const getStatRangeKey = (stat) => {
    if (stat === 'damage') return 'damageRange';
    if (stat === 'sps') return 'spaRange';
    if (stat === 'range') return 'rangeRange';
    if (stat === 'placement') return 'damageRange'; // Placement uses damage range
    return 'damageRange';
  };

  // Calculate final stats
  const calculateStats = () => {
    if (!selectedUnit) return null;

    const baseStats = selectedUnit.baseStats;
    const levelMultiplier = 1 + (level - 1) * 0.1; // 10% increase per level
    const upgradeMultiplier = 1 + (upgrade - 1) * 0.05; // 5% increase per upgrade

    let finalStats = {};

    // Apply base calculations with level and upgrade multipliers
    Object.keys(baseStats).forEach(stat => {
      if (stat === 'placement') {
        // Placement cost is fixed and doesn't change
        finalStats[stat] = baseStats[stat];
        return;
      }

      // Use percentage-based multiplier instead of fixed grade multiplier
      const percentageBonus = statPercentages[stat] / 100;
      const statMultiplier = 1 + percentageBonus;
      let statValue = baseStats[stat] * levelMultiplier * upgradeMultiplier * statMultiplier;

      // Apply trait effects
      if (selectedTrait) {
        const trait = availableTraits.find(t => t.id === selectedTrait);
        if (trait && trait.effects[stat]) {
          statValue *= trait.effects[stat];
        }
      }

      finalStats[stat] = Math.round(statValue);
    });

    return finalStats;
  };

  // Update calculated stats when any parameter changes
  useEffect(() => {
    setCalculatedStats(calculateStats());
  }, [selectedUnit, level, upgrade, statGradeSelection, statPercentages, selectedTrait]);

  // Handle unit selection
  const handleUnitSelect = (unit) => {
    setSelectedUnit(unit);
    setUnitSearch(unit.name);
    setLevel(1);
    setUpgrade(1);
    setStatGradeSelection({
      damage: 'B',
      range: 'B',
      sps: 'B'
    });
    setStatPercentages({
      damage: 9,
      range: 4.75,
      sps: 4.75
    });
    setSelectedTrait(null);
  };

  // Handle stat grade change
  const handleStatGradeChange = (statType, grade) => {
    setStatGradeSelection(prev => ({
      ...prev,
      [statType]: grade
    }));
    
    // Set percentage to middle of the grade range
    const rangeKey = getStatRangeKey(statType);
    const gradeData = statGrades[grade];
    const [min, max] = gradeData[rangeKey];
    const middleValue = (min + max) / 2;
    
    setStatPercentages(prev => ({
      ...prev,
      [statType]: Number(middleValue.toFixed(2))
    }));
  };

  // Handle percentage change
  const handlePercentageChange = (statType, percentage) => {
    setStatPercentages(prev => ({
      ...prev,
      [statType]: Number(percentage)
    }));
    
    // Update grade based on percentage
    const rangeKey = getStatRangeKey(statType);
    let newGrade = 'D';
    
    for (const [grade, data] of Object.entries(statGrades)) {
      const [min, max] = data[rangeKey];
      if (percentage >= min && percentage <= max) {
        newGrade = grade;
        break;
      }
    }
    
    setStatGradeSelection(prev => ({
      ...prev,
      [statType]: newGrade
    }));
  };

  // Handle trait selection
  const handleTraitSelect = (traitId) => {
    setSelectedTrait(prev => prev === traitId ? null : traitId);
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'text-red-600';
      case 'Exclusive': return 'text-pink-400';
      case 'Secret': return 'text-red-400';
      case 'Mythical': return 'text-purple-400';
      case 'Legendary': return 'text-yellow-400';
      case 'Epic': return 'text-blue-400';
      case 'Rare': return 'text-green-400';
      default: return 'text-anime-secondary';
    }
  };

  const getRarityBorder = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'border-red-600';
      case 'Exclusive': return 'border-pink-400';
      case 'Secret': return 'border-red-400';
      case 'Mythical': return 'border-purple-400';
      case 'Legendary': return 'border-yellow-400';
      case 'Epic': return 'border-blue-400';
      case 'Rare': return 'border-green-400';
      default: return 'border-anime-secondary';
    }
  };

  const getRarityBg = (rarity) => {
    switch (rarity) {
      case 'Vanguard': return 'bg-red-600/20';
      case 'Exclusive': return 'bg-pink-400/20';
      case 'Secret': return 'bg-red-400/20';
      case 'Mythical': return 'bg-purple-400/20';
      case 'Legendary': return 'bg-yellow-400/20';
      case 'Epic': return 'bg-blue-400/20';
      case 'Rare': return 'bg-green-400/20';
      default: return 'bg-anime-secondary/20';
    }
  };

  return (
    <div className="min-h-screen" style={{ backgroundColor: '#0F0F1A' }}>
      {/* Professional Header */}
      <div className="bg-gradient-to-r from-anime-dark to-anime-darker border-b border-white/10" style={{ 
        backgroundImage: 'linear-gradient(to right, #1A1A2E, #0F0F1A)',
        borderBottomColor: 'rgba(255, 255, 255, 0.1)'
      }}>
        <div className="max-w-7xl mx-auto px-6 py-12">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-black mb-4" style={{ color: '#ffffff' }}>
              Unit Stat Calculator
            </h1>
            <p className="text-lg max-w-2xl mx-auto" style={{ color: '#CBD5E0' }}>
              Optimize your builds with precise stat calculations, grade tuning, and trait combinations
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-12 gap-8">
          {/* Left Sidebar - Unit Selection */}
          <div className="col-span-12 lg:col-span-4 xl:col-span-3">
            <div className="backdrop-blur-xl border rounded-2xl p-6 sticky top-6" style={{
              backgroundColor: 'rgba(255, 255, 255, 0.05)',
              borderColor: 'rgba(255, 255, 255, 0.1)'
            }}>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-bold" style={{ color: '#ffffff' }}>Unit Selection</h2>
                {selectedUnit && (
                  <button
                    onClick={() => {
                      setSelectedUnit(null);
                      setUnitSearch('');
                      setLevel(1);
                      setUpgrade(1);
                      setStatGradeSelection({
                        damage: 'B',
                        range: 'B',
                        sps: 'B'
                      });
                      setStatPercentages({
                        damage: 9,
                        range: 4.75,
                        sps: 4.75
                      });
                      setSelectedTrait(null);
                    }}
                    className="text-sm transition-colors"
                    style={{ color: '#00D9FF' }}
                    onMouseEnter={(e) => e.target.style.color = '#ffffff'}
                    onMouseLeave={(e) => e.target.style.color = '#00D9FF'}
                  >
                    Clear
                  </button>
                )}
              </div>

              {!selectedUnit ? (
                <>
                  {/* Search Input */}
                  <div className="mb-6">
                    <input
                      type="text"
                      placeholder="Search units..."
                      value={unitSearch}
                      onChange={(e) => setUnitSearch(e.target.value)}
                      className="w-full border rounded-xl px-4 py-3 focus:outline-none transition-colors"
                      style={{
                        backgroundColor: 'rgba(255, 255, 255, 0.05)',
                        borderColor: 'rgba(255, 255, 255, 0.1)',
                        color: '#ffffff'
                      }}
                      onFocus={(e) => e.target.style.borderColor = '#00D9FF'}
                      onBlur={(e) => e.target.style.borderColor = 'rgba(255, 255, 255, 0.1)'}
                    />
                  </div>

                  {/* Unit Grid */}
                  <div className="space-y-3 max-h-96 overflow-y-auto">
                    {filteredUnits.map(unit => (
                      <button
                        key={unit.id}
                        onClick={() => handleUnitSelect(unit)}
                        className="w-full p-4 bg-white/5 hover:bg-white/10 border border-white/10 hover:border-anime-primary/50 rounded-xl transition-all text-left group"
                      >
                        <div className="flex items-center space-x-3">
                          <div className="w-12 h-12 bg-anime-primary/20 rounded-lg flex items-center justify-center flex-shrink-0">
                            <div className="w-6 h-6 bg-anime-primary rounded"></div>
                          </div>
                          <div className="flex-1 min-w-0">
                            <h3 className="font-semibold text-white truncate group-hover:text-anime-primary transition-colors">
                              {unit.name}
                            </h3>
                            <div className="flex items-center space-x-2 mt-1">
                              <span className={`text-xs px-2 py-1 rounded ${getRarityColor(unit.rarity)} ${getRarityBg(unit.rarity)}`}>
                                {unit.rarity}
                              </span>
                              <span className="text-xs text-white/60">{unit.element}</span>
                            </div>
                          </div>
                        </div>
                      </button>
                    ))}
                  </div>
                </>
              ) : (
                /* Selected Unit Display */
                <div className="space-y-6">
                  <div className="text-center">
                    <div className="w-20 h-20 bg-anime-primary/20 rounded-2xl mx-auto mb-4 flex items-center justify-center">
                      <div className="w-10 h-10 bg-anime-primary rounded-xl"></div>
                    </div>
                    <h3 className="text-lg font-bold text-white mb-2">{selectedUnit.name}</h3>
                    <div className="flex items-center justify-center space-x-4 text-sm">
                      <span className={`px-3 py-1 rounded-lg ${getRarityColor(selectedUnit.rarity)} ${getRarityBg(selectedUnit.rarity)}`}>
                        {selectedUnit.rarity}
                      </span>
                      <span className="text-white/60">{selectedUnit.element}</span>
                    </div>
                  </div>

                  {/* Base Stats Preview */}
                  <div className="bg-white/5 rounded-xl p-4">
                    <h4 className="text-sm font-semibold text-white/80 mb-3">Base Stats</h4>
                    <div className="grid grid-cols-2 gap-3">
                      {Object.entries(selectedUnit.baseStats).map(([stat, value]) => (
                        <div key={stat} className="text-center">
                          <div className="text-xs text-white/60 uppercase tracking-wider">{stat}</div>
                          <div className="text-sm font-bold text-anime-primary">{value}</div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Main Content Area */}
          <div className="col-span-12 lg:col-span-8 xl:col-span-9">
            {!selectedUnit ? (
              /* Welcome State */
              <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-12 text-center">
                <div className="w-24 h-24 bg-anime-primary/20 rounded-3xl mx-auto mb-6 flex items-center justify-center">
                  <svg className="w-12 h-12 text-anime-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                  </svg>
                </div>
                <h2 className="text-2xl font-bold text-white mb-4">Select a Unit to Begin</h2>
                <p className="text-white/60 text-lg max-w-md mx-auto">
                  Choose a unit from the sidebar to start calculating optimized stat builds
                </p>
              </div>
            ) : (
              /* Configuration Tabs */
              <div className="space-y-8">
                {/* Level & Upgrade Controls */}
                <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-6">
                  <h3 className="text-xl font-bold text-white mb-6">Level & Upgrade</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div>
                      <div className="flex items-center justify-between mb-4">
                        <label className="text-white font-medium">Level</label>
                        <span className="text-2xl font-bold text-anime-primary">{level}</span>
                      </div>
                      <input
                        type="range"
                        min="1"
                        max="100"
                        value={level}
                        onChange={(e) => setLevel(parseInt(e.target.value))}
                        className="w-full h-3 bg-white/10 rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-white/50 mt-2">
                        <span>1</span>
                        <span>100</span>
                      </div>
                    </div>
                    
                    <div>
                      <div className="flex items-center justify-between mb-4">
                        <label className="text-white font-medium">Upgrade</label>
                        <span className="text-2xl font-bold text-anime-accent">{upgrade}</span>
                      </div>
                      <input
                        type="range"
                        min="1"
                        max="10"
                        value={upgrade}
                        onChange={(e) => setUpgrade(parseInt(e.target.value))}
                        className="w-full h-3 bg-white/10 rounded-lg appearance-none cursor-pointer slider"
                      />
                      <div className="flex justify-between text-sm text-white/50 mt-2">
                        <span>1</span>
                        <span>10</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Stat Grades */}
                <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-6">
                  <h3 className="text-xl font-bold text-white mb-6">Stat Grades & Percentages</h3>
                  
                  <div className="space-y-8">
                    {Object.keys(selectedUnit.baseStats).filter(stat => stat !== 'placement').map(stat => {
                      const rangeKey = getStatRangeKey(stat);
                      const currentGrade = statGrades[statGradeSelection[stat]];
                      const [minRange, maxRange] = currentGrade[rangeKey];
                      
                      return (
                        <div key={stat} className="space-y-4">
                          {/* Stat Header */}
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-3">
                              <h4 className="text-lg font-semibold text-white capitalize">{stat}</h4>
                              <span className={`px-3 py-1 rounded-lg text-sm font-bold ${currentGrade.color} ${currentGrade.bgColor}`}>
                                {statGradeSelection[stat]}
                              </span>
                            </div>
                            <div className="text-right">
                              <div className="text-2xl font-bold text-anime-primary">{statPercentages[stat]}%</div>
                              <div className="text-sm text-white/50">Current Bonus</div>
                            </div>
                          </div>

                          {/* Grade Selection */}
                          <div className="grid grid-cols-8 gap-2">
                            {Object.keys(statGrades).map(grade => (
                              <button
                                key={grade}
                                onClick={() => handleStatGradeChange(stat, grade)}
                                className={`p-3 rounded-lg font-bold text-xs transition-all ${
                                  statGradeSelection[stat] === grade
                                    ? `${statGrades[grade].color} ${statGrades[grade].bgColor} border-2`
                                    : 'text-white/60 bg-white/5 hover:bg-white/10 border-2 border-transparent'
                                }`}
                                style={{
                                  borderColor: statGradeSelection[stat] === grade ? 
                                    statGrades[grade].color.replace('text-', '').replace('-400', '') : 'transparent'
                                }}
                              >
                                <div className="w-6 h-6 mx-auto mb-1 flex items-center justify-center">
                                  <img 
                                    src={`/images/Stats/Stat${grade === 'Z+' ? 'ZPLUS' : grade}.webp`} 
                                    alt={grade}
                                    className="w-full h-full object-contain"
                                    onError={(e) => {
                                      e.target.style.display = 'none';
                                      e.target.nextSibling.style.display = 'block';
                                    }}
                                  />
                                  <div className="w-4 h-4 bg-anime-primary/30 rounded hidden"></div>
                                </div>
                                <div>{grade}</div>
                              </button>
                            ))}
                          </div>

                          {/* Percentage Slider */}
                          <div className="space-y-3">
                            <div className="flex items-center justify-between text-sm">
                              <span className="text-white/60">Range: {minRange}% - {maxRange}%</span>
                              <div className="flex items-center space-x-4 text-xs">
                                <span className="text-white/50">Chance: <span className={currentGrade.color}>{currentGrade.chance}</span></span>
                                <span className="text-white/50">100% Potential: <span className={currentGrade.color}>{currentGrade.potential}</span></span>
                              </div>
                            </div>
                            
                            <input
                              type="range"
                              min={minRange}
                              max={maxRange}
                              step="0.01"
                              value={statPercentages[stat]}
                              onChange={(e) => handlePercentageChange(stat, parseFloat(e.target.value))}
                              className="w-full h-3 bg-white/10 rounded-lg appearance-none cursor-pointer slider"
                            />
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>

                {/* Traits Selection */}
                <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-6">
                  <div className="flex items-center justify-between mb-6">
                    <h3 className="text-xl font-bold text-white">Traits</h3>
                    <span className="text-sm text-white/60">Select 1 trait (optional)</span>
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {availableTraits.map(trait => (
                      <button
                        key={trait.id}
                        onClick={() => handleTraitSelect(trait.id)}
                        className={`p-4 rounded-xl border transition-all text-left ${
                          selectedTrait === trait.id
                            ? `${trait.color} ${trait.bgColor} ${trait.borderColor} border-2`
                            : 'text-white/80 bg-white/5 border-white/10 hover:bg-white/10 hover:border-white/20'
                        }`}
                      >
                        <div className="flex items-start space-x-3">
                          <div className="w-10 h-10 flex items-center justify-center flex-shrink-0">
                            <img 
                              src={`/images/Traits/${trait.name}.webp`} 
                              alt={trait.name}
                              className="w-full h-full object-contain"
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'flex';
                              }}
                            />
                            <div className="w-8 h-8 bg-anime-primary/30 rounded-lg hidden items-center justify-center">
                              <div className="w-4 h-4 bg-anime-primary rounded"></div>
                            </div>
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-2">
                              <div className="font-bold">{trait.name}</div>
                              <div className="text-xs opacity-75">{trait.rarity}</div>
                            </div>
                            <div className="text-xs opacity-90 leading-relaxed">{trait.description}</div>
                          </div>
                        </div>
                      </button>
                    ))}
                  </div>
                </div>

                {/* Final Stats */}
                {calculatedStats && (
                  <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-6">
                    <h3 className="text-xl font-bold text-white mb-6">Final Stats</h3>
                    
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                      {Object.entries(calculatedStats).map(([stat, value]) => (
                        <div key={stat} className="bg-white/5 rounded-xl p-4 text-center">
                          <div className="text-sm text-white/60 uppercase tracking-wider mb-2">{stat}</div>
                          <div className="text-3xl font-black text-anime-primary mb-2">{value.toLocaleString()}</div>
                          <div className="text-xs text-white/50">
                            Base: {selectedUnit.baseStats[stat].toLocaleString()}
                          </div>
                          {stat !== 'placement' && (
                            <div className="text-sm font-bold text-anime-accent mt-1">
                              +{(((value / selectedUnit.baseStats[stat]) - 1) * 100).toFixed(1)}%
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
