import React, { useState, useEffect } from 'react';

// Enhanced animated background component
const CalculatorBackground = () => (
  <div className="fixed inset-0 pointer-events-none overflow-hidden">
    {/* Floating particles */}
    {[...Array(20)].map((_, i) => (
      <div
        key={i}
        className="absolute rounded-full bg-anime-primary/20 animate-pulse"
        style={{
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          width: `${Math.random() * 10 + 5}px`,
          height: `${Math.random() * 10 + 5}px`,
          animationDelay: `${Math.random() * 5}s`,
          animationDuration: `${Math.random() * 3 + 2}s`
        }}
      />
    ))}

    {/* Moving gradients */}
    <div className="absolute inset-0 bg-gradient-to-br from-anime-primary/5 via-transparent to-anime-secondary/5 animate-pulse" />
    <div className="absolute inset-0 bg-gradient-to-tl from-anime-accent/3 via-transparent to-anime-purple/3" 
         style={{ animation: 'float 15s ease-in-out infinite' }} />

    {/* Grid pattern */}
    <div className="absolute inset-0 opacity-[0.03]"
         style={{
           backgroundImage: `
             linear-gradient(rgba(0, 217, 255, 0.5) 1px, transparent 1px),
             linear-gradient(90deg, rgba(0, 217, 255, 0.5) 1px, transparent 1px)
           `,
           backgroundSize: '40px 40px'
         }} />
  </div>
);

const Calculator = () => {
  // Unit database with base stats
  const unitsDatabase = [
    {
      id: 1,
      name: 'Goku Ultra Instinct',
      rarity: 'Mythical',
      element: 'Holy',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1250,
        range: 8.5,
        sps: 1.2,
        placement: 35000
      }
    },
    {
      id: 2,
      name: 'Rimuru Tempest',
      rarity: 'Legendary', 
      element: 'Water',
      tier: 'Good',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 980,
        range: 7.8,
        sps: 1.5,
        placement: 28000
      }
    },
    {
      id: 3,
      name: 'Madara Uchiha',
      rarity: 'Mythical',
      element: 'Fire',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1180,
        range: 9.2,
        sps: 1.1,
        placement: 32000
      }
    },
    {
      id: 4,
      name: 'Ichigo Bankai',
      rarity: 'Legendary',
      element: 'Spark',
      tier: 'SemiMeta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1100,
        range: 8.0,
        sps: 1.3,
        placement: 30000
      }
    },
    {
      id: 5,
      name: 'Naruto Six Paths',
      rarity: 'Mythical',
      element: 'Nature',
      tier: 'Meta',
      image: '/api/placeholder/150/150',
      baseStats: {
        damage: 1220,
        range: 8.8,
        sps: 1.25,
        placement: 33000
      }
    },
    {
      id: 6,
      name: 'Saitama',
      rarity: 'Secret',
      element: 'Unbound',
      tier: 'Broken',
      image: '/api/placeholder/150/150',
      baseStats: {
