                            <span className={`text-lg font-bold ${gradeData.textColor}`}>
                              {currentGrade}
                            </span>
                            <div className="text-sm" style={{ color: '#CBD5E0' }}>
                              {statPercentages[statType]}%
                            </div>
                          </div>
                        </div>
                        <input
                          type="range"
                          min="0"
                          max={maxRange}
                          step="0.01"
                          value={statPercentages[statType]}
                          onChange={(e) => handleStatPercentageChange(statType, e.target.value)}
                          className="w-full h-3 bg-white/10 rounded-lg appearance-none cursor-pointer slider"
                        />
                        <div className="flex justify-between text-xs" style={{ color: 'rgba(255, 255, 255, 0.5)' }}>
                          <span>0%</span>
                          <span>{maxRange}%</span>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          )}

          {/* Traits Tab */}
          {activeTab === 'traits' && selectedUnit && (
