                  }}>
                    <img
                      src={selectedUnit.image}
                      alt={selectedUnit.name}
                      className="w-16 h-16 rounded-lg"
                    />
                    <div>
                      <h4 className="font-bold" style={{ color: '#ffffff' }}>
                        {selectedUnit.name}
                      </h4>
                      <p className="text-sm opacity-75" style={{ color: '#CBD5E0' }}>
                        Level {level} â€¢ Upgrade {upgrade}
                      </p>
                    </div>
                  </div>

                  {selectedTrait && (
                    <div className="flex items-center gap-4 p-4 rounded-xl" style={{
                      backgroundColor: 'rgba(0, 217, 255, 0.1)'
                    }}>
                      <img
                        src={availableTraits.find(t => t.id === selectedTrait).icon}
                        alt={availableTraits.find(t => t.id === selectedTrait).name}
                        className="w-12 h-12 rounded-lg"
                      />
                      <div>
                        <h5 className="font-medium" style={{ color: '#00D9FF' }}>
                          {availableTraits.find(t => t.id === selectedTrait).name}
                        </h5>
                        <p className="text-sm opacity-75" style={{ color: '#CBD5E0' }}>
                          {availableTraits.find(t => t.id === selectedTrait).description}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Final Stats */}
              <div className="backdrop-blur-xl border rounded-2xl p-8" style={{
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                borderColor: 'rgba(255, 255, 255, 0.1)'
              }}>
                <h3 className="text-xl font-bold mb-6" style={{ color: '#ffffff' }}>
                  Final Statistics
                </h3>
                <div className="space-y-4">
                  {Object.entries(calculatedStats).map(([stat, value]) => (
                    <div key={stat} className="flex justify-between items-center p-4 rounded-xl" style={{
                      backgroundColor: 'rgba(255, 255, 255, 0.05)'
                    }}>
                      <span className="font-medium capitalize" style={{ color: '#CBD5E0' }}>
                        {stat === 'sps' ? 'SPS' : stat}
                      </span>
                      <span className="text-xl font-bold" style={{ color: '#00D9FF' }}>
                        {stat === 'placement' ? value.toLocaleString() : value}
                        {stat === 'range' ? 'm' : stat === 'sps' ? '/s' : ''}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Calculator;
