import React, { useState } from 'react';

const UnitList = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRarity, setSelectedRarity] = useState('All');

  // Sample units data
  const units = [
    { id: 1, name: 'Goku Ultra Instinct', rarity: 'Mythic', element: 'Light', tier: 'S+' },
    { id: 2, name: 'Rimuru Tempest', rarity: 'Legendary', element: 'Water', tier: 'S' },
    { id: 3, name: 'Madara Uchiha', rarity: 'Mythic', element: 'Fire', tier: 'S' },
    { id: 4, name: 'Ichigo Bankai', rarity: 'Legendary', element: 'Dark', tier: 'A' },
    { id: 5, name: 'Saitama', rarity: 'Mythic', element: 'Physical', tier: 'S+' },
    { id: 6, name: 'Gojo Satoru', rarity: 'Legendary', element: 'Light', tier: 'S' },
    { id: 7, name: 'Anos Voldigoad', rarity: 'Mythic', element: 'Dark', tier: 'S' },
    { id: 8, name: 'Tanjiro Water', rarity: 'Epic', element: 'Water', tier: 'A' },
  ];

  const rarities = ['All', 'Mythic', 'Legendary', 'Epic', 'Rare'];

  const filteredUnits = units.filter(unit => {
    const matchesSearch = unit.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesRarity = selectedRarity === 'All' || unit.rarity === selectedRarity;
    return matchesSearch && matchesRarity;
  });

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Mythic': return 'text-red-400 border-red-400';
      case 'Legendary': return 'text-yellow-400 border-yellow-400';
      case 'Epic': return 'text-purple-400 border-purple-400';
      case 'Rare': return 'text-blue-400 border-blue-400';
      default: return 'text-anime-light border-anime-light';
    }
  };

  const getTierColor = (tier) => {
    switch (tier) {
      case 'S+': return 'bg-red-500 text-white';
      case 'S': return 'bg-yellow-500 text-white';
      case 'A': return 'bg-green-500 text-white';
      default: return 'bg-anime-light text-anime-dark';
    }
  };

  return (
    <div className="min-h-screen py-8 px-4">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold gradient-text mb-4">Unit Database</h1>
          <p className="text-anime-light text-lg">
            Browse all available units and their stats
          </p>
        </div>

        {/* Search and Filter Controls */}
        <div className="anime-card mb-8">
          <div className="flex flex-col md:flex-row gap-4">
            {/* Search Input */}
            <div className="flex-1">
              <input
                type="text"
                placeholder="Search units..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-4 py-3 bg-anime-gray/50 border border-anime-gray-light/30 rounded-lg text-anime-light placeholder-anime-light/50 focus:outline-none focus:border-anime-primary transition-colors"
              />
            </div>
            
            {/* Rarity Filter */}
            <div className="md:w-48">
              <select
                value={selectedRarity}
                onChange={(e) => setSelectedRarity(e.target.value)}
                className="w-full px-4 py-3 bg-anime-gray/50 border border-anime-gray-light/30 rounded-lg text-anime-light focus:outline-none focus:border-anime-primary transition-colors"
              >
                {rarities.map(rarity => (
                  <option key={rarity} value={rarity} className="bg-anime-gray">
                    {rarity} Rarity
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Units Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredUnits.map((unit) => (
            <div key={unit.id} className="unit-card group">
              {/* Unit Image Placeholder */}
              <div className="relative overflow-hidden rounded-lg mb-4">
                <div className="w-full h-48 bg-gradient-to-br from-anime-purple/20 to-anime-accent/20 flex items-center justify-center">
                  <span className="text-6xl">‚öîÔ∏è</span>
                </div>
                <div className="absolute top-2 right-2">
                  <span className={`px-2 py-1 rounded text-xs font-semibold ${unit.tier === 'S+' ? 'bg-anime-primary' : unit.tier === 'S' ? 'bg-anime-orange' : 'bg-anime-secondary'} text-white`}>
                    {unit.tier}
                  </span>
                </div>
                <div className="absolute top-2 left-2">
                  <span className={`px-2 py-1 rounded text-xs font-semibold bg-anime-dark/80 border ${getRarityColor(unit.rarity)}`}>
                    {unit.rarity}
                  </span>
                </div>
              </div>

              {/* Unit Info */}
              <div className="space-y-2">
                <h3 className="text-lg font-semibold text-anime-light group-hover:text-anime-primary transition-colors">
                  {unit.name}
                </h3>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-anime-secondary">
                    {unit.element}
                  </span>
                  <span className={`px-2 py-1 rounded text-xs font-semibold ${getTierColor(unit.tier)}`}>
                    Tier {unit.tier}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* No Results */}
        {filteredUnits.length === 0 && (
          <div className="anime-card text-center py-12">
            <div className="text-4xl mb-4">üîç</div>
            <h3 className="text-xl font-semibold text-anime-light mb-2">No Units Found</h3>
            <p className="text-anime-secondary">
              Try adjusting the search terms or filters
            </p>
          </div>
        )}

        {/* Results Count */}
        <div className="text-center mt-8">
          <p className="text-anime-secondary">
            Showing {filteredUnits.length} of {units.length} units
          </p>
        </div>
      </div>
    </div>
  );
};

export default UnitList;
